INSERT INTO `objects` VALUES (1,1,'2014-06-08 22:15:31','2021-06-10 15:44:31',10,'Home',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'home',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								\r\n																								',_binary '\r\n																								<br>																								',_binary '\r\n																								\r\n																								'),(2,1,'2014-06-08 22:15:42','2014-07-31 05:46:19',80,'_Settings',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'settings',NULL,NULL,NULL,NULL,NULL,'','',NULL),(3,1,'2014-06-08 22:15:57','2021-06-10 15:44:24',10,'Main',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'main',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								\r\n																								',_binary '\r\n																								<br>																								',_binary '\r\n																								\r\n																								'),(4,1,'2014-06-08 22:16:07','2021-06-10 15:45:01',40,'Gallery',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'gallery',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								',_binary '\r\n																								',_binary '\r\n																								'),(5,1,'2014-06-08 22:16:16','2021-06-10 15:44:53',50,'Apartment',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'apartment',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								',_binary '\r\n																								',_binary '\r\n																								'),(6,1,'2014-06-08 22:16:31','2021-06-10 15:45:10',60,'On Our Mind',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'on-our-mind',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								',_binary '\r\n																								',_binary '\r\n																								'),(7,1,'2014-06-08 22:16:42','2021-06-10 15:44:40',30,'Calendar',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'calendar',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								\r\n																								',_binary '\r\n																								<br>																								',_binary '\r\n																								\r\n																								'),(8,1,'2014-06-08 22:17:03','2021-06-23 11:24:47',10,'The Wattis Institute',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'the-wattis-institute',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								',_binary '<a href=\"main\" style=\"color:#FFF;border-bottom: solid 3px #FFF;\">The Wattis Institute <br></a>. We’re located in San Francisco, and are part of the <a href=\"http://www.cca.edu\" target=\"new\">California College of the Arts</a>, which is a few blocks away. Our building sits on the unceded territories of Chochenyo and Ramaytush Ohlone peoples. and then some more ...',_binary '\r\n																								'),(9,1,'2014-06-08 22:17:17','2021-06-10 17:01:38',40,'Program',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'program',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								\r\n																								\r\n																								\r\n																								',_binary 'The 2020-2021 Capp Street Artist-in-Residence is <a href=\"/gallery/raven-chacon-radio-coyote\">Raven Chacon</a>. His online and FM radio station, Radio Coyote, is now live.',_binary '\r\n																								\r\n																								\r\n																								\r\n																								'),(10,1,'2014-06-08 22:17:32','2021-06-24 16:33:05',20,'News',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'news',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								\r\n																								',_binary '<div id=\"news\" class=\"redwhite\"></div><br><br>',_binary '\r\n																								\r\n																								'),(11,1,'2014-06-08 22:17:50','2021-06-10 16:50:35',80,'Event',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'event',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								\r\n																								',_binary '\r\n																								\r\n																								On <a href=\"/calendar/wednesday-march-24-2021\">March 24</a>, Dr. Charisse Burden-Stelly talks about Black anticapitalists. Click <a href=\"https://wattis.org/calendar\">here</a> for all other upcoming events.<br>',_binary '\r\n																								\r\n																								'),(12,1,'2014-06-08 22:18:03','2014-08-19 08:06:31',35,'Tuesday, September 9',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'tuesday-september-9',NULL,NULL,NULL,NULL,NULL,'',_binary '<a href=\'\'>We’re RE-OPENING soon</a> . . . On September 9, we invite you to a public reception from 7 to 9 pm.',NULL),(13,1,'2014-06-08 22:18:20','2014-08-18 07:22:49',60,'Weather',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'weather',NULL,NULL,NULL,NULL,NULL,'',_binary 'We’re at 360 Kansas Street (between 16th & 17th). <i><span id=\'rss\'><span></i> _-_-_ <a href=\'visit\'>Visit the Wattis</a>.',NULL),(14,1,'2014-06-08 22:18:35','2014-07-29 18:41:08',110,'Upcoming',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'upcoming',NULL,NULL,NULL,NULL,NULL,'',_binary '<div class=\"scheduleContainer\"><span id=\"sentence10\">In the next three months, there are <a href=\'calendar\'>six events</a> planned. <canvas id=\"canvas15\" width=\"20\" height=\"22\">6</canvas></span></div>',NULL),(15,1,'2014-06-08 22:19:04','2020-12-27 23:59:16',150,'Elsewhere',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'elsewhere',NULL,NULL,NULL,NULL,NULL,'',_binary 'Meanwhile, have a look at the <a href = \'https://riversinstitute.org/\'><br>Rivers Institute</a>, a great new project out of New Orleans.',NULL),(16,1,'2014-06-08 22:19:27','2021-06-10 16:50:08',110,'Archive',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'archive',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								',_binary '\r\n																								<a href=\"archive\">Consult the archive</a> (of exhibitions and events).',_binary '\r\n																								'),(17,1,'2014-06-08 22:19:36','2014-06-08 22:32:18',110,'Newsletter',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'newsletter',NULL,NULL,NULL,NULL,NULL,'',_binary '<div class=\"newsletterContainer\"><span id=\"sentence11\"><i>Our newsletter</i> . . . <br /><form enctype=\'multipart/form-data\' action=\'\'method=\'post\' style=\'margin: 0; padding: 0;\'><textarea name=\'sender\' cols=\'30\' rows=\'1\'class=\'Mono\'></textarea><input name=\'subscribe\' type=\'submit\' value=\'Subscribe\' /></form></span></div><br>',NULL),(18,1,'2014-06-08 22:19:57','2021-06-10 16:48:34',80,'Social',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'social',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								',_binary '\r\n																								<img src=\"media/fti.gif\"> <a href=\"follow\">here</a>•_°',_binary '\r\n																								'),(19,1,'2014-06-13 04:55:10','2014-07-31 05:46:53',100,'_Dev',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'dev',NULL,NULL,NULL,NULL,NULL,'','',NULL),(20,1,'2014-06-13 04:55:22','2014-07-23 06:02:55',NULL,'Punctuation',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'punctuation',NULL,NULL,NULL,NULL,NULL,_binary '∞',_binary '!\"#$%&()*+,\\-.\\/:;<=>?@\\[\\\\\\]^_`\\{|\\}~°•´∞±≤≥¿¡«»–—“”‘’÷‹›¦−×⁏⁑⁔‿⁀⁐⁖∗∘∙∴∵≀∪∩⊂⊃┌┐<br><br>*<br>∞<br>{}<br>[]<br>“People\" like to sit still, but bodies don’t. Mirrors have a way of putting the arm where the leg should be and the arm where the leg should be.  { Joan Jonas } has been spent a lot of time taking herself apart and putting herself back together again in the company of others. + * - ?<br><br>Joan Jonas, Vertical Roll from >1971, and Standing Sideways, >1978. Both deal with the way the body conforms to story telling. Art historian {Pamela Lee} hosts a conversation before and after the screening. Drinks will be served. <br><br>Next Tuesday, September 24<br>>7pm<br><br>The Wattis<br>360 Kansas Street<br>SF, CA 94013<br>',NULL),(21,1,'2014-06-23 16:44:10','2014-06-28 11:40:01',NULL,'Sign',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'sign',NULL,NULL,NULL,NULL,NULL,_binary '//* . . . This is <a href=\'\'>The Wattis</a>. We’re a few blocks away from California College of the Arts, <<⅂ at 360 Kansas Street (between 16th & 17th), SF, CA 94013. Today, it is kind of foggy and currently 78° F. II, III, IV, V: 12–7; VI : 12–5. ((())) www.wattis.org\r\n\r\nWe are open T-F 12 to 7 / S 12 to 5.',_binary '//* . . . This is <a href=\'\'>The Wattis<\\/a>. We’re a few blocks away from California College of the Arts, <<⅂ at 360 Kansas Street (b/t 16th & 17th), SF, CA 94013. Today, it is kind of foggy and currently 78° F. ((())) www.wattis.org<br><br>We are open T-F 12 to 7 / S 12 to 5. ',NULL),(22,1,'2014-07-09 16:47:13','2021-03-30 04:27:26',5,'Read about us',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'read-about-us',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								 . . . our mission, approach, and history',_binary '<br>																								<a href=\"http://www.wattis.org\">CCA Wattis Institute for Contemporary Arts</a> is a nonprofit exhibition venue and research institute dedicated to contemporary art and ideas. The Wattis is a laboratory for testing the future of contemporary art through temporary exhibitions, public events, and in-depth research. It is part of the California College of the Arts in San Francisco.<br><br>Our Mission:<br>What and how can we learn from artists today? <br><br>Our Approach:<br>The Wattis supports artists who take risks and experiment with new ideas. We provide a public forum to established, emerging, and under-recognized artists who challenge our ways of understanding the art of our current moment.<br><br>We currently work with artists in two distinct ways: as an <a href=\"/read-about-us/the-exhibition-program\">exhibition space</a>, we show new and existing work by artists from around the world; and as a <a href=\"/read-about-us/the-research-seasons\">research institute</a>, we commit an entire year to reflecting on the questions posed by a single artist’s work. <br><br>Our History:<br>CCA Wattis Institute for Contemporary Arts was founded in 1998 at <a href=\"http://www.cca.edu\" target=\"new\">California College of the Arts</a> in San Francisco. Initiated by Larry Rinder, it was originally called the CCAC Institute of Exhibitions and Public Programming. In 2002, it was renamed the CCA Wattis Institute for Contemporary Arts. In 2013, it moved from CCA\'s main campus to its own building a few blocks away. Subsequently to Rinder\'s directorship, the Wattis has been led by Ralph Rugoff (2000 - 2006), Jens Hoffmann (2006 - 2012), and Anthony Huberman (2013 - present). <br><br>Upon its founding in 1998, the Wattis also absorbed <a href=\"http://www.wattis.org/capp\">Capp Street Project</a>, one of the earliest and longest-running artist-in-residence programs in the country, founded in 1983 by Ann Hatch. Building on that history, the Wattis Institute hosts a <a href=\"/capp-street-project/capp-street-artists-1984-a\">Capp Street Artist-in-Residence</a> every year and invites an artist to come live and work in San Francisco for a semester, to teach a graduate seminar at CCA, and to present an exhibition. More about that<a href=\"http://www.wattis.org/capp\"> here</a>.<br><br>The Wattis Institute has also collaborated with students and faculty members in other programs at CCA, including the Graduate Program in <a href=\"https://www.cca.edu/academics/graduate/curatorial-practice\" target=\"new\">Curatorial Practice</a>, the Graduate Program in <a href=\"https://www.cca.edu/academics/graduate/fine-arts\" target=\"new\">Fine Arts</a>, and the Graduate Program in <a href=\"https://www.cca.edu/academics/graduate/visual-critical-studies\" target=\"new\">Visual and Critical Studies</a>, among others.<br><br><i>The CCA Wattis Institute program is generously supported by The Andy Warhol Foundation for the Visual Arts, The Westridge Foundation, Daniel and Manizeh Rimer, San Francisco Grants for the Arts, Penny and Jim Coulter, Gina and Stuart Peterson, Jonathan Gans and Abigail Turin, and CCA Wattis Institute’s Curator’s Forum. Phyllis C. Wattis was the generous founding patron.</i><br><br>All CCA campuses are located in Huichin and Yelamu, also known as Oakland and San Francisco, on the unceded territories of Chochenyo and Ramaytush Ohlone peoples.<br>',_binary '\r\n																								'),(23,1,'2014-07-09 16:51:05','2021-03-30 04:30:11',30,'Visit the Wattis',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'visit-the-wattis',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								. . . address, hours, and directions.',_binary '<br>																								The Wattis Institute is located in San Francisco. It is open and free to the public. The address is:<br><br><span class=\"helvetica medium\">CCA Wattis Institute<br>360 Kansas Street<br>(between 16th &amp; 17th Streets)<br>San Francisco, CA 94103<br>USA<br><br>Tuesday–Saturday, noon–6pm<br>Closed Sunday &amp; Monday<br><br><br>In response to the developing COVID-19 health threat and crisis, the Wattis galleries and offices are temporary closed until further notice. All visits, tours, and events are postponed or canceled at this time, Please look here and on our social media channels for updates<br><br>The Wattis is accessible to all visitors. <br><br>+1 415 355 9670<br><br>Please direct postal mail to:<br>CCA Wattis Institute<br>1111 Eighth Street<br>San Francisco, CA 94107<br></span> <br><br>///Directions:<br><br><span class=\"helvetica medium\">BUS 22 (to 16th &amp; Kansas)<br><br>BUS 19 or 10 (to 16th &amp; Rhode Island)<br><br>BART (to 16th &amp; Mission, then transfer to BUS 22 towards Potrero Hill)<br><br>Street parking is available. A parking garage is across the street from the gallery.<br><br><iframe src=\"https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3154.057437904075!2d-122.40611128535062!3d37.765251379761196!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x808f7fcd3e38f0d9%3A0xdaea0fa38bcb832e!2sCCA+Wattis+Institute!5e0!3m2!1sen!2sus!4v1512796878953\" width=\"600\" height=\"450\" frameborder=\"0\" style=\"border:0\" allowfullscreen=\"\"></iframe><br></span><br><br>',_binary '\r\n																								'),(24,1,'2014-07-09 16:53:39','2021-03-30 04:30:21',35,'Contact the Wattis',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'contact-the-wattis',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								. . . get in touch with the staff and find our mailing address.',_binary '<br>																								Please address all postal and FedEx mail to:<br><br><span class=\"helvetica medium\">CCA Wattis Institute<br>1111 Eighth Street<br>San Francisco, CA 94107 <br><br>Note, our physical address is:<br>CCA Wattis Institute<br>360 Kansas Street<br>San Francisco, CA 94103<br>USA<br><br>Phone +1 415 355 9670<br>Fax +1 415 355 9676<br>E-mail wattis@cca.edu</span><br><br>///<span class=\"helvetica medium\">Geada Alagha<br>Social Media Manager<br><a href=\"mailto:geada.alagha@cca.edu\">geada.alagha@cca.edu</a><br><br>Zalika Azim<br>Research Fellow<br><a href=\"mailto:zalika.azim@cca.edu\">zalika.azim@cca.edu</a><br><br>Selam Bekele<br>Curatorial Fellow<br><a href=\"mailto:selam@cca.edu\">selam@cca.edu</a><br><br>Jeanne Gerrity<br>Deputy Director and Head of Publications<br><a href=\"mailto:jgerrity@cca.edu\">jgerrity@cca.edu</a><br><br>Anthony Huberman<br>Director and Chief Curator<br><a href=\"mailto:anthony@cca.edu\">anthony@cca.edu</a><br><br>Taryn Lott<br>Media Relations Manager<br><a href=\"mailto:tlott@cca.edu\">tlott@cca.edu</a><br><br>Carleigh McDonald<br>Individual Gifts / Memberships<br>Director, Leadership Gifts, CCA<br><a href=\"mailto:c.mcdonald@cca.edu\">c.mcdonald@cca.edu</a><br>*contact for Curator\'s Forum memberships<br><br>Emily Markert<br>Curatorial Fellow<br><a href=\"mailto:emily.markert@cca.edu\">emily.markert@cca.edu</a><br><br>Kim Nguyen<br>Curator<br><a href=\"mailto:kim@cca.edu\">kim@cca.edu</a><br><br>Addy Rabinovitch<br>Operations Coordinator<br><a href=\"mailto:arabinovitch@cca.edu\">arabinovitch@cca.edu</a><br><br>Joe Sanders<br>Head of Installation and Exhibition Design<br><a href=\"mailto:joseph.sanders@cca.edu\">joseph.sanders@cca.edu</a><br><br>Diego Villalobos<br>Exhibitions Manager<br><a href=\"mailto:diego@cca.edu\">diego@cca.edu</a><br><br>Calder Yates<br>Foundation and Institutional Gifts<br>Institutional Giving Manager, CCA<br><a href=\"mailto:calder@cca.edu\">calder@cca.edu</a><br><br>Graphic Design: <br><br>Companion-Platform (Lexi Visco &amp; Calvin Rocchio)<br>Print Design<br><a href=\"http://www.companion-platform.org\">www.companion-platform.org</a><br><br>David Reinfurt<br>Web Design<br><a href=\"http://www.o-r-g.com\">www.o-r-g.com</a><br><a href=\"http://www.davidreinfurt.com\">www.davidreinfurt.com</a><br><br></span>',_binary '\r\n																								'),(25,1,'2014-07-09 16:54:39','2021-03-30 04:31:05',55,'Become a Member',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'become-a-member',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								. . . This really helps us do what we do. Thank you.',_binary '<br>																								Members of the Wattis <span style=\"color:#ff0000\">Curator\'s Forum</span> receive special access to curators from around the world, studio visits and Q&amp;As with artists, private gatherings at the most vibrant art collections in the Bay Area, and organized trips to exhibitions and studios worldwide.<br><br>The current COVID-19 pandemic has altered our ability to offer in-person events, but <span style=\"color:#ff0000\">members will receive many benefits</span>, including intimate virtual gatherings with invited guests, virtual studio visits, virtual exhibition previews, and more. <br><br>Most importantly, joining as a member provides <span style=\"color:#ff0000\">a deeper and more enriching relationship with the Wattis, artists, and our community.</span><br><br>Gifts of any amount represent a vital support for the Wattis’s exhibitions and programs.<br><br>Click <a href=\"https://www.givecampus.com/campaigns/8206/donations/new?pdesignation=ccawattisinstitute&amp;pvalue=5000 \" target=\"new\">here</a> to join.<br><br><i>For more information about the Wattis Membership Program please contact Carleigh McDonald at c.mcdonald@cca.edu</i>.<br><br><br>///<span class=\"helvetica medium\">CURATOR\'S FORUM — $5,000 / yr<br><br>+ Invitation for you and a guest to private exhibition previews<br><br>+ Invitation for you and a guest to six additional private events (discussions with international curators, artist studio visits, or local collection visits) <br><br>+ 10% off limited edition artworks<br><br>+ Reserved seating to limited access public events<br><br>+ Invitation to organized trips to visit exhibitions, artists, and collections in cities around the world<br><br>+ Private tour of all Wattis exhibitions with Director<br><br>+ Receive ongoing \"Notes from the Field,\" behind-the-scenes curatorial research notes by Wattis curators about exhibitions and studio visits worldwide<br><br>(<span style=\"color:#ff0000\">Note: due to COVID-19, benefits will continue to evolve to best accommodate our community and keep everyone safe</span>)<br><br></span>',_binary '\r\n																								'),(26,1,'2014-07-09 16:54:58','2021-03-30 04:31:26',60,'Buy Limited Editions',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'buy-limited-editions',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								. . . for an affordable price, own a work of art by major national and international artists.',_binary '<br>																								All proceeds for the sale of editions go directly toward the support of Wattis exhibitions, programs, and research.<br><br>Please visit our <a href=\"https://secure.touchnet.net/C20080_ustores/web/store_cat.jsp?STOREID=6&amp;CATID=19&amp;SINGLESTORE=true\" target=\"blank\">shop</a> to purchase editions.<br><br>For direct sales or additional information, please contact Jeanne Gerrity at <a href=\"mailto:jgerrity@cca.edu\">jgerrity@cca.edu</a>',_binary '\r\n																								'),(27,1,'2014-07-09 16:55:20','2021-03-30 04:30:38',40,'Buy Catalogues',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'buy-catalogues',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								. . . shop for books and exhibition catalogues related to past exhibitions.',_binary '<br>																								All proceeds for the sale of catalogues go directly toward the support of Wattis exhibitions, programs, and research.<br><br>For direct sales please visit <a href=\"https://secure.touchnet.net/C20080_ustores/web/store_cat.jsp?STOREID=6&amp;CATID=21&amp;SINGLESTORE=true\">our online store.</a>',_binary '\r\n																								'),(28,1,'2014-07-09 16:55:42','2021-03-30 04:32:17',72,'Follow the Wattis',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'follow-the-wattis',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								. . . on Twitter, Facebook, and Instagram.',_binary '<br>																								<a href=\"https://www.facebook.com/pages/Wattis-Institute-for-Contemporary-Arts/35655507493?fref=nf\" target=\"new\">on Facebook . . .</a><br><br><a href=\"http://instagram.com/wattisarts\" target=\"new\">on Instagram . . .</a><br><br><a href=\"https://twitter.com/WattisArts\" target=\"new\">on Twitter . . .</a><br><br><a href=\"http://visitor.r20.constantcontact.com/d.jsp?llr=p5q4x4qab&amp;p=oi&amp;m=1117756834767&amp;sit=fmbork8ib&amp;f=697249c3-8765-4e88-af24-28ce4c573ca9\" target=\"new\">Subscribe to newsletter . . .</a>',_binary '\r\n																								'),(29,1,'2014-07-09 16:56:13','2021-03-30 04:32:00',70,'Intern at the Wattis',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'intern-at-the-wattis',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								 . . . get hands-on experience working in a contemporary art center.',_binary '<br>																								Being an intern at the Wattis is an opportunity to experience a contemporary arts institution firsthand.<br><br>There is no set application deadline for an internship, and inquiries are welcome throughout the year. We offer internships roughly coinciding with each academic term (January to May and September to December) and over the summer (June through August). The internships are designed to cover a three-month period, with a minimum commitment of two days per week. <br><br>To apply, please send your cover letter and CV to Jeanne Gerrity: <a href=\"mailto:jgerrity@cca.edu\">jgerrity@cca.edu</a>.<br><br>The Wattis is not currently accepting internship applications. If you are a CCA student, please email <a href=\"mailto:jgerrity@cca.edu\">jgerrity@cca.edu</a> to inquire about workstudy opportunities.',_binary '\r\n																								'),(30,1,'2014-07-09 16:56:31','2021-03-30 04:30:00',27,'Consult the Archive',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'consult-the-archive',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								. . . information on past exhibitions and past events (1998–present).',_binary '<br>																								The Wattis Institute has produced exhibitions, where artworks are \"on view,\" as well as year-long research seasons, where a single artist is \"on our mind\" for an entire year. Each forms a separate (but overlapping) archive:<br><br><a href=\"/consult-the-archive/past-exhibitions-2014-a-present?alt=1\">Past exhibitions</a> (2014– present) . . .<br><br><a href=\"/consult-the-archive/2014-a-research-seasons?alt=1\">Past research seasons</a> (2014– present) . . . <br><br><a href=\"/consult-the-archive/2014-a-events?alt=1\">Past events</a> (2014– present) . . . <br><br>Click <a href=\"/read-about-us/the-exhibition-program\">here</a> for more information about our exhibition program and <a href=\"/read-about-us/the-research-seasons\">here</a> for more information about our research seasons.<br><br>The Wattis Institute\'s previous website archives all programs between 1998 and 2014:<br> <br><a href=\"http://archive.wattis.org/exhibitions/archive\" target=\"new\">Past exhibitions</a> (1998–2014) . . .<br><br><a href=\"http://archive.wattis.org/calendar/archive\" target=\"new\">Past events </a>(1998–2014) . . .',_binary '\r\n																								'),(31,1,'2014-07-09 16:56:53','2021-03-30 04:31:44',65,'Capp Street Project',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'capp-street-project',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								. . . founded by Ann Hatch in 1983, Capp Street Project became part of the Wattis in 1998.',_binary '<br>																								Founded in San Francisco in 1983 by Ann Hatch, Capp Street Project was the first visual arts residency in the United States dedicated solely to the creation and presentation of new art installations and gave more than 100 local, national, and international artists the opportunity to create new work through its residency and public exhibition programs. <br><br>In 1998, Capp Street Project became part of the Wattis Institute, where it has evolved into a residency program. Every year, an artist spends three to four months in San Francisco meeting the local art community, working on new projects, and teaching at CCA. Without any fixed or predetermined outcome, each artist develops a relevant and appropriate way to share their work with an audience, which could involve producing an exhibition, presenting a performance, curating an exhibition, or publishing a piece of writing, among other forms. <br><br>///A list of Capp Street Artists (1984–present) is <a href=\"/capp-street-project/capp-street-artists-1984-a\">available here</a>.<br><br>The <a href=\"https://vault.cca.edu/hierarchy.do?topic=1b9b7218-074b-35fd-12e8-d97d811bc408&amp;page=1\">Capp Street Project Archive</a> is housed in the Meyer Library on the Oakland campus of California College of the Arts. Click <a href=\"https://vault.cca.edu/hierarchy.do?topic=1b9b7218-074b-35fd-12e8-d97d811bc408&amp;page=1\">here</a> to access it online.<br><br>The archive documents the process, intention, and realization of installations and temporary projects sponsored by Capp Street Project since 1983. Under the auspices of the college\'s libraries, the archive preserves and organizes materials relating to the projects and makes these materials available for study and research. Through the <a href=\"https://vault.cca.edu/hierarchy.do?topic=1b9b7218-074b-35fd-12e8-d97d811bc408&amp;page=1\">archive\'s website</a>, present-day audiences may view projects as they once existed, learn about the artists who created them, and experience these works anew.',_binary '\r\n																								'),(32,1,'2014-07-09 19:17:49','2019-06-19 01:07:14',100,'Markus Schinwald: <i>New Work</i> (2014)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'markus-schinwald-new-work-2014',NULL,'2014-09-09 00:00:00','2014-12-13 00:00:00',NULL,NULL,'',_binary '<a href=\"\">Markus Schinwald</a> shows a site-specific installation of new and recent work.<br><br>He gives inanimate objects personalities of their own: they have good moods, bad moods, nervous tics, and psychological baggage. His paintings, sculptures, and installations have “issues,” in the way that most relationships do. Conversely, he also imagines a world where a state of mind could give rise to an object. “What if,” the work asks, “a moment of anxiety could generate a neck brace?” Clearly, this gives a whole new meaning to what we say when we talk about prosthetics. (...<a href=\'http://wattis.org/_Outbox/Print/Schinwald-web.pdf\' target=\'new\'>continue reading</a>).<br><br><i>This exhibition is jointly organized by CCA Wattis Institute and the San Francisco Museum of Modern Art. It is part of SFMOMA\'s New Work series, which is generously supported by the museum\'s Collectors Forum, the founding patron of the series. The exhibition is also made possible by Adriane Iann and Christian Stolz, Carlie Wilmans, Patricia W. Fitzpatrick, Nancy and R. Patrick Forster, Station to Station, and the Austrian Cultural Forum New York, with special thanks to Giò Marconi.</i><br><br>This exhibition is curated by Jenny Gheith and Anthony Huberman.<br><br><a href = \'/buy-limited-editions/markus-schinwald\'>Click here</a> to learn more about our limited edition by Markus Schinwald.<br><br>Press: <br><br><a href=\'http://www.wattis.org/media/00896.pdf\' target=\'new\'><i>Artforum</i></a>, by Jeffrey Kasther<br><a href=\'http://www.wattis.org/media/00897.pdf\' target=\'new\'><i>Contemporary Art Daily</i></a><br><a href=\'http://www.wattis.org/media/00898.pdf\' target=\'new\'><i>Curiously Direct</i></a>, by Jackie Im<br><a href=\'http://www.wattis.org/media/00899.pdf\' target=\'new\'><i>Mousse</i></a><br><a href=\'http://www.wattis.org/media/00617.pdf\' target=\'new\'><i>San Francisco Cottages and Gardens</i></a>, by Alisa Carroll<br><a href=\'http://www.wattis.org/media/00900.pdf\' target=\'new\'><i>SciArt in America</i></a>, by Joe Ferguson<br><a href=\'http://www.wattis.org/media/00901.pdf\' target=\'new\'><i>San Francisco Chronicle</i></a>, by Julie Guthrie<br><a href=\'http://www.wattis.org/media/00902.pdf\' target=\'new\'><i>SFWeekly</i></a>, by Emily Wilson',_binary '<object id=\"flashObj\" width=\"480\" height=\"270\" classid=\"clsid:D27CDB6E-AE6D-11cf-96B8-444553540000\" codebase=\"http://download.macromedia.com/pub/shockwave/cabs/flash/swflash.cab#version=9,0,47,0\"><param name=\"movie\" value=\"http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1\" /><param name=\"bgcolor\" value=\"#FFFFFF\" /><param name=\"flashVars\" value=\"videoId=3872821749001&linkBaseURL=http%3A%2F%2Fwww.sfmoma.org%2Fexplore%2Fmultimedia%2Fvideos%2F598&playerID=1277164621001&playerKey=AQ~~,AAAAsKKH5Lk~,NLi44cDINh_bjjEkXE6_A0uM4mBjvuYA&domain=embed&dynamicStreaming=true\" /><param name=\"base\" value=\"http://admin.brightcove.com\" /><param name=\"seamlesstabbing\" value=\"false\" /><param name=\"allowFullScreen\" value=\"true\" /><param name=\"swLiveConnect\" value=\"true\" /><param name=\"allowScriptAccess\" value=\"always\" /><embed src=\"http://c.brightcove.com/services/viewer/federated_f9?isVid=1&isUI=1\" bgcolor=\"#FFFFFF\" flashVars=\"videoId=3872821749001&linkBaseURL=http%3A%2F%2Fwww.sfmoma.org%2Fexplore%2Fmultimedia%2Fvideos%2F598&playerID=1277164621001&playerKey=AQ~~,AAAAsKKH5Lk~,NLi44cDINh_bjjEkXE6_A0uM4mBjvuYA&domain=embed&dynamicStreaming=true\" base=\"http://admin.brightcove.com\" name=\"flashObj\" width=\"480\" height=\"270\" seamlesstabbing=\"false\" type=\"application/x-shockwave-flash\" allowFullScreen=\"true\" allowScriptAccess=\"always\" swLiveConnect=\"true\" pluginspage=\"http://www.macromedia.com/shockwave/download/index.cgi?P1_Prod_Version=ShockwaveFlash\"></embed></object>'),(33,1,'2014-07-27 15:42:56','2020-10-30 06:06:50',199,'Ed Ruscha',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ed-ruscha',NULL,NULL,NULL,NULL,NULL,_binary '<i>Self-Portrait of My Forearm 1960 and Self-Portrait of My Forearm 2014</i>, 1960-2014\r\nDiptych. Gelatin silver print on Ilford Multigrade paper, mounted in window mat\r\n20.5 x 11.75 inches\r\nSigned and numbered edition of 35\r\n\r\n$4,500\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=104&SINGLESTORE=true\" target=\"_blank\">Purchase this edition here.</a>',_binary 'Esteemed West Coast pop artist Ed Ruscha playfully upends the genre of self-portraiture in a twofold fashion. Ruscha not only foregoes the category’s traditional composition—a straight-on depiction of the face or body—but his diptych of two images, which depict nothing more than the artist\'s forearm and are separated by more than 50 years, offers little evidence of aging or change.<br><br><a href=\'editions\'><</a>',NULL),(34,1,'2014-07-27 15:44:42','2020-10-30 06:08:03',200,'Ryan Gander',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ryan-gander',NULL,NULL,NULL,NULL,NULL,_binary '<i>…and their integrity was uncompromising</i>, 2013\r\nIncomplete A3 jigsaw, with 33% of its pieces missing\r\nSigned and numbered edition of 15 with 8 AP’s\r\n\r\n$1,000\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=105&SINGLESTORE=true\" target=\"_blank\">Purchase this edition here.</a>',_binary 'As is traditionally the case with challenging works of conceptual art, <i>…and their integrity was uncompromising</i> by Ryan Gander consists of simple materials, here, the jumbled pieces of a partial puzzle, upon which Gander handwrote a quote from the ancient Spartan king Menelaus: “May it keep the wolves in the hills and the women in our beds.” A limited edition of 15, this intellectual and unconventional work probes the meaning and effects of incompletion, all the while eliciting frustration and amusement alike from the viewer. <br><br><a href=\'editions\'><</a>',NULL),(35,1,'2014-07-27 15:45:48','2020-10-30 06:10:16',202,'Claire Fontaine',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'claire-fontaine',NULL,NULL,NULL,NULL,NULL,_binary '<i>Untitled (Two hundred and fifty redeemed cans)</i>, 2013\r\nAluminum ingot bar\r\n12.5 x 3.75 x 2.5 x 2.75 inches\r\nSigned and numbered certificate, edition of 25\r\n\r\n$1,200\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=107&SINGLESTORE=true\" target=\"_blank\">Purchase this edition here.</a>',_binary 'The French collective Claire Fontaine studies the function of found material in contemporary art, and this untitled edition of 25 constitutes a particularly thought-provoking investigation. Each of these aluminum ingot bars corresponds to the weight of a bag of recycled aluminum cans that Claire Fontaine previously installed in an exhibition at San Francisco’s CCA Wattis Institute for Contemporary Arts. This continual reuse of the cans—first as consumer products, then as elements of a gallery installation, and finally as condensed sculptures—pointedly demonstrates the various value systems driving contemporary culture.<br><br><a href=\'editions\'><</a>',NULL),(36,1,'2014-07-27 15:46:42','2018-03-15 22:03:17',207,'Jordan Wolfson',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'jordan-wolfson',NULL,NULL,NULL,NULL,NULL,_binary '<i>Untitled</i>, 2009\r\nDigital print mounted on aluminum \r\n23 1/4 x 31 x 1/16 inches\r\nSigned and numbered edition of 10\r\n\r\nSOLD OUT',_binary 'Despite its straightforward, quotidian subject matter—green blades of grass, a tennis shoe, and a plastic cup of water—this untitled photograph by Jordan Wolfson constitutes a confounding, almost surrealistic, visual puzzle. Printed in a limited edition of ten, Wolfson presents a delightfully inexplicable image (why exactly is the cup in the shoe?), prompting the viewer to “do the interpretive legwork,” just as he intended.<br><br><a href=\'editions\'><</a>',NULL),(37,1,'2014-07-27 15:48:07','2018-08-31 22:14:09',205,'Catherine Opie',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'catherine-opie',NULL,NULL,NULL,NULL,NULL,_binary '<i>Bravo</i>, 2011\r\nChromogenic print\r\n12 x 16 inches\r\nSigned and numbered edition of 100\r\n\r\nSOLD OUT',_binary 'Catherine Opie\'s Bravo was a part of the exhibit <i>More American Photographs</i> at the CCA Wattis Institute for Contemporary Art in San Francisco, which commissioned twelve photographers to travel the country and document the land and its people. Modeled after the Depression-era photography program of the Farm Security Administration, the commissions update the nation\'s visual imagery, creating a broad picture of the contemporary socio-economic moment. Opie\'s photographs from the series focus on the idea of community. She drew her inspiration from stores in her immediate geographic area, Los Angeles, to cultivate feelings of both connection and disconnection.<br><br><a href=\'editions\'><</a>',NULL),(38,1,'2014-07-27 15:50:06','2020-10-30 06:11:36',203,'Jonathan Monk',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'jonathan-monk',NULL,NULL,NULL,NULL,NULL,_binary '<i>Grey / Gray </i>, 2014\r\n7\" Colored vinyl record \r\nThis is a variable edition. Each record within the edition of 100 is visually unique.\r\nSigned and numbered edition of 100\r\n\r\n$250\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=108&SINGLESTORE=true\" target=\"_blank\">Purchase this edition here.</a>',_binary 'In this work, each record within the edition is visually unique and comes with two records. Side A is the sound of black paint being mixed with white. Side B is the sound of white paint being mixed with black. One is in a black sleeve and the other in a white one, housed within a clear plastic sleeve with a printed sticker and a small offset printed certificate signed in pencil and painted with grey or gray finger prints.<br><br><a href=\'editions\'><</a>',NULL),(39,1,'2014-07-27 15:50:50','2018-03-15 22:03:26',206,'John Baldessari',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'john-baldessari',NULL,NULL,NULL,NULL,NULL,_binary '<i>Stairway, Coat and Person</i>, 2011\r\nArchival inkjet print\r\n20 x 16 inches\r\nSigned and numbered edition of 120\r\n\r\nSOLD OUT',_binary 'John Baldessari is known for his use of appropriation, erasure, alteration, and montage to interrupt a given narrative and create entirely new meaning out of existing imagery. The layered, often humorous compositions carry disparate connotations, underscoring the relative nature of language.<br><br><a href=\'editions\'><</a>',NULL),(40,1,'2014-07-27 15:52:04','2020-10-30 06:12:45',204,'Paulina Olowska',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'paulina-olowska',NULL,NULL,NULL,NULL,NULL,_binary '<i>Study on International Creative Prism</i>, 2012\r\nChromogenic print\r\n11 x 17 inches\r\nSigned and numbered edition of 50\r\n\r\n$350\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=109&SINGLESTORE=true\" target=\"_blank\">Purchase this edition here.</a>',_binary 'In this collage-like print, Olowska incorporates imagery from diverse sources, reflecting points of continuity and exchange between Eastern European and Western ideas of modernism.<br><br><a href=\'editions\'><</a>',NULL),(41,1,'2014-07-27 15:54:02','2020-10-30 06:14:24',208,'Walead Beshty',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'walead-beshty',NULL,NULL,NULL,NULL,NULL,_binary '<i>Allen, South Dakota, Median Income $7,578: United States Post Office, August 1, 2011, (Cancelled by Jens Hoffmann, August 16, 2011), Fisher Island, Florida, Median Income $236,238: United States Post Office, August 23, 2011 (Cancelled by Jens Hoffman, August 26, 2011)</i>\r\nTwo kodak semi-gloss prints\r\n4 x 6 inches\r\nSigned and numbered certificate, edition of 50\r\n\r\n$600\r\n\r\n<a href=\" https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=110&SINGLESTORE=true\" target=\"_blank\">Purchase this edition here.</a>',_binary 'For this work, experimental photographer Walead Beshty followed Roy Stryker\'s method of editing and asked Wattis Institute director Jens Hoffmann to “kill” two of his photographs with large black circles. These two images of post offices in South Dakota and Florida highlight the economic disparity between the regions.<br><br><a href=\'editions\'><</a>',NULL),(42,1,'2014-07-27 15:54:52','2018-03-15 22:06:23',208,'Laura Owens',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'laura-owens',NULL,NULL,NULL,NULL,NULL,_binary '<i>Untitled</i>, 2012\r\nHandmade book made with durotone paper, binder\'s board, cloth, Flashe, acrylic, and ink\r\n15 x 19.5 x 1 inches\r\nSigned and numbered edition of 12\r\n\r\nSOLD OUT',_binary 'LA-based artist Laura Owens\' imagery references a wide and imaginative range of subjects and techniques in her practice. Moving with ease between high and low, personal and social, Owens investigates the formal issues of painting through a highly personal blend of abstract and representational imagery.<br><br><a href=\'editions\'><</a>',NULL),(43,1,'2014-07-27 15:55:54','2020-10-30 06:16:12',210,'Collier Schorr',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'collier-schorr',NULL,NULL,NULL,NULL,NULL,_binary '<i>Young Rider</i>, 2008\r\nArchival pigment print\r\n11 x 11.25 inches\r\nSigned and numbered edition of 100\r\n\r\n$250 \r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=112&SINGLESTORE=true\" target=\"_blank\">Purchase this edition here.</a>',_binary 'A limited-edition print from the exhibition <i>More American Photographs</i> at the CCA Wattis Institute for Contemporary Art in San Francisco, CA, Collier Schorr\'s Young Rider portrays a young cowboy at the threshold of manhood, his adolescent fragility still palpable. Schorr\'s photographs often look at gender construction, youth, and role playing. <br><br>As a part of this exhibit, which commissioned twelve photographers to travel the country and document the land and its people in the tradition of the Depression-era Farm Security Administration, Schorr\'s work present two visions of America that exist simultaneously: the privileged desire for fame and fortune meets with its underbelly, the imminent drive for survival.<br><br><a href=\'editions\'><</a>',NULL),(44,1,'2014-07-27 15:56:49','2020-10-30 06:15:22',209,'Larry Clark',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'larry-clark',NULL,NULL,NULL,NULL,NULL,_binary '<i>Adam, Marfa, TX</i>, 2011\r\nArchival ink jet print\r\n17.3 x 24 inches\r\nSigned and numbered edition of 10\r\n\r\n$3,500 \r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=111&SINGLESTORE=true\" target=\"_blank\">Purchase this edition here.</a>',_binary 'Larry Clark\'s <i>Adam, Marfa, TX</i> was a part of the exhibit <i>More American Photographs</i> at the CCA Wattis Institute for Contemporary Art in San Francisco, which commissioned twelve photographers to travel the country and document the land and its people. Modeled after the Depression-era photography program of the Farm Security Administration, the commissions update the nation\'s visual imagery, creating a broad picture of the contemporary socio-economic moment. Here, Clark captures a young skater looking as doleful as the panda bear on his t-shirt. Clark\'s photographs are often candid forays into American subcultures.<br><br><a href=\'editions\'><</a>',NULL),(45,1,'2014-07-27 16:08:36','2020-10-30 21:33:42',343,'The Ninth Page: Etel Adnan\'s Journalism 1972-74\r\n$20',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'the-ninth-page-etel-adnan-s-journalism-1972-74-20',NULL,NULL,NULL,NULL,NULL,_binary 'Graduate Program in Curatorial Practice at CCA and CCA Wattis Institute for Contemporary Arts, Edited by Julian Myers-Szupinska and Heidi Rabben\r\nDesigned by Jon Sueda, 2013\r\nPaperback, 128 pages. \r\n\r\n$20.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=141&SINGLESTORE=true\" target=\"_blank\">Purchase this publication here.</a>\r\n',_binary 'Published to accompany the exhibition <a href=\'http://archive.wattis.org/exhibitions/words-and-places-etel-adnan\' target=\'new\'>Words and Places Etel Adnan</a>, <i>The Ninth Page: Etel Adnan\'s Journalism 1972-74 </i>, collects and translates articles written by Adnan during the early 1970s for the Beirut-based francophone newspaper Al-Safa. In addition to the articles, the publication includes a Foreword by Leigh Markopoulos, an introductory essay by Simone Fattal, an interview with Adnan by Jesi Khadivi and Heidi Rabben, and a newly commissioned essay by Andrew Weiner, all responding to Adnanʼs journalism and its fraught sociopolitical context. It also includes a copy of the exhibition guide with writing on the exhibition by Antonia Marsh and on Adnan\'s writing by Rebecca Roy. <br><br>The articles selected for <i>The Ninth Page</i> challenge preconceptions about Beirut\'s political climate in this moment while also offering up a portrait of an entire cosmopolitan, international worldview that is social and cultural as often as it is political. The diverse subjects Adnan covers document the rich cultural scene of Beirut on the brink of civil war, a political cataclysm addressed with great force later in Adnanʼs landmark books <i>Sitt Marie Rose (1978)</i> and <i>The Arab Apocalypse (1980)</i>. By focusing on her time as a journalist, <i>The Ninth Page</i> fills a discursive gap in the chronicling of Adnan\'s written history, enriching her painting and writing practices. Images of the original newspaper articles are also projected in the exhibition space.<br>Words and Places: Etel Adnan is the first large-scale institutional exhibition of work by the Lebanese writer, poet, and painter Etel Adnan, spanning six decades of her artistic practice. Born in Beirut in 1925 to a Christian Greek mother and a Muslim Syrian father, Adnan has spent her life between places––Beirut, Paris, and the Bay Area––negotiating their different cultures and languages, as well as her distinctive position among them. This experience of displacement deeply informs her work, which similarly ranges between mediums and formats.<br><br>This catalogue is available for academic purposes only. <br><br><a href=\'http://archive.wattis.org/exhibitions/words-and-places-etel-adnan\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(46,1,'2014-07-27 16:09:54','2020-10-30 21:34:22',344,'When Attitudes Became Form Become Attitudes\r\n$40',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'when-attitudes-became-form-become-attitudes-40',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2013\r\nPaperback, 287 pages.\r\n\r\n$40.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=142&SINGLESTORE=true\">Purchase this publication here.</a>',_binary 'Following the “office-binder” format of the original When Attitudes Become Form publication from 1969, the exhibition catalogue When Attitudes Became Form Become Attitudes includes over 80 pages of images and information on the contemporary artists included in the <a href=\'http://archive.wattis.org/exhibitions/when-attitudes-became-form-become-attitudes\' target=\'new\'>exhibition</a>, as well as new perspectives on Szeemann’s iconic exhibition. With texts by Jens Hoffmann, Julian Myers-Szupinska, Constance Lewallen, and Christian Rattemeyer, as well as an interview between Hoffmann and Szeemann, When Attitudes Became Form Become Attitudes examines the art of the present through looking towards the past. Contact sheets by the photographers Janos Kender and Harry Shunk offer unprecedented views of Szeemann’s exhibition, while images by Walead Beshty provide a contemporary counter-point documenting the 2012 exhibition at the CCA Wattis.<br><br>The publication includes bios, images, and works by the following artists:<br><br>Zarouhie Abdalian<br>Pablo Accinelli<br>Meriç Algün Ringborg<br>Jennifer Allora and Guillermo Calzadilla<br>Jonathas de Andrade<br>Kathryn Andrews<br>Nazgol Ansarinia<br>Nicolás Bacal<br>Christopher Badger<br>Alessandro Balteo Yazbeck<br>Yto Barrada<br>Taysir Batniji<br>James Beckett<br>Nina Beier<br>Erick Beltrán<br>Walead Beshty<br>Cezary Bodzianowski<br>Matthew Buckingham<br>Johanna Calle<br>Arabella Campbell<br>Juan Capistran<br>Mariana Castillo Deball<br>Etienne Chambaud<br>Marcelo Cidade<br>Claire Fontaine<br>Nicolás Consuegra<br>Abraham Cruzvillegas<br>Alexandre da Cunha<br>Maria Eichhorn<br>Michael Elmgreen and Ingar Dragset<br>Cevdet Erek<br>Annika Eriksson<br>Lara Favaretto<br>Aurélien Froment<br>Simon Fujiwara<br>Meschac Gaba<br>Dani Gal<br>Ryan Gander<br>Mario Garcia Torres<br>João Maria Gusmão and Pedro Paiva<br>Alexander Gutke<br>Jeppe Hein<br>Emily Jacir<br>Maryam Jafri<br>Alicja Kwade<br>Luisa Lambri<br>Adriana Lara<br>Tim Lee<br>Mateo López<br>Renata Lucas<br>Marie Lund<br>Kris Martin<br>Vincent Meessen<br>Simon Dybbroe Møller<br>Jonathan Monk<br>Shahryar Nashat<br>Roman Ondák<br>Fernando Ortega<br>Christodoulos Panayiotou<br>Nicolás Paris<br>Pratchaya Phinthong<br>Amalia Pica<br>Kirsten Pieroth<br>Wilfredo Prieto<br>Pablo Rasgado<br>Nicolás Robbio<br>Will Rogan<br>Pamela Rosenkranz<br>Fabrice Samyn<br>Kim Schoenstadt<br>Tino Sehgal<br>Sean Snyder<br>Mark Soo<br>Mateo Tannatt<br>Ron Terada<br>Hank Willis Thomas<br>Jan Timme<br>Clarissa Tossin<br>Guido van der Werve<br>Natasha Wheat<br>Akram Zaatari<br><br><a href=\'http://archive.wattis.org/exhibitions/when-attitudes-became-form-become-attitudes\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(47,1,'2014-07-27 16:10:29','2020-10-30 21:35:06',345,'Americana\r\n$25',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'americana-25',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2012\r\nHardcover, 240 pages.\r\n\r\n$25.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=143&SINGLESTORE=true\">Purchase this publication here.</a>',_binary 'This catalog is a culmination and collection of <a href=\'http://archive.wattis.org/exhibitions/americana\' target=\'new\'>exhibitions</a> consisting of 55 displays co-organized by the Wattis Institute and CCA\'s Graduate Program in Curatorial Practice.<br><br>Through artworks, historical artifacts, curiosities, and other elements, Americana focuses on overlooked and little-known aspects of each state. All presentations take place in the same exhibition space, which is configured in the shape of the continental United States.<br><br>Americana examines the states as they are today, looking at how America\'s social and political imperatives condition the production, presentation, and interpretation of art and exhibition making. The brisk pace of the 50 displays reflects the varied and constantly changing fabric of this relatively young country and its multilayered, shifting national identity.<br><br>The title is a reference to an exhibition of the same name that was curated by the artist collaborative Group Material at the 1985 biennial of the Whitney Museum of American Art, New York. Group Material also focused on art and elements of mass culture that they understood as overlooked, forgotten, and outside the mainstream in order to investigate critically how museums and exhibitions assist in the formation of American identity.<br><br>This catalog is designed by Jon Sueda / Stripe.<br><br><a href=\'http://archive.wattis.org/exhibitions/americana\' target=\'new\'>Go to exhibitions</a><br><br><a href=\'catalogs\'><</a>',NULL),(48,1,'2014-07-27 16:11:05','2019-05-20 21:53:03',346,'The Way Beyond Art: Wide White Space\r\nSold Out',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'the-way-beyond-art-wide-white-space-sold-out',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2012\r\nPaperback, 104 pages.\r\nSOLD OUT',_binary 'The Way Beyond Art: Wide White Space investigates graphic design\'s evolving relationship with the practice of exhibition making as it intersects with the visual arts and the work of both artists and curators. This <a href=\'http://archive.wattis.org/exhibitions/way-beyond-art-wide-white-space\' target=\'new\'>exhibition</a> is realized in collaboration with CCA\'s undergraduate Graphic Design Program, the Graduate Program in Design, and Jon Sueda. Sueda is a Graphic Design professor at CCA and founder of the San Francisco design practice Stripe. Wide White Space is the second installment in The Way Beyond Art, a series of exhibitions at the Wattis that closely integrates the institute\'s programs with CCA\'s non-fine arts faculty and curricula.<br><br>The title Wide White Space refers to the name of a radical art space in Antwerp that, though it existed for only a decade, came to define contemporary art in the late 1960s and early 1970s. This exhibition likewise aims to investigate the possibilities for how empty space, whether the white cube or the blank page, can be transformed into something more complex. The name of the larger program, The Way Beyond Art, refers to the title of a book written by the visionary German art historian Alexander Dorner, who advocated in the early 20th century for a closer dialogue among different artistic disciplines. He is best known for his collaboration with the Constructivist artist El Lissitzky on the Abstract Cabinet (1927) at the Landesmuseum Hannover, Germany, a unique, specially constructed space that explored a new form of multidisciplinarity produced by juxtaposing art, fashion, design, film, and literature.<br><br>This catalog was produced by Jon Sueda / Stripe.<br><br><a href=\'http://archive.wattis.org/exhibitions/way-beyond-art-wide-white-space\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(49,1,'2014-07-27 16:11:56','2020-10-30 21:35:37',347,'Give Them the Picture\r\nSold Out',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'give-them-the-picture-sold-out',NULL,NULL,NULL,NULL,NULL,_binary 'Graduate Program in Curatorial Practice at CCA and CCA Wattis Institute for Contemporary Arts, 2011\r\nPaperback, 203 pages\r\n\r\nSOLD OUT',_binary 'Give Them the Picture is a literary extension of the Curatorial Practice thesis <a href=\'http://archive.wattis.org/exhibitions/god-only-knows-who-audience\' target=\'new\'>exhibition</a> on view in the Wattis from April 21, 2011 to July 02, 2011. Rather than a traditional exhibition catalog, it is an anthology of essays taken from La Mamelle and ART COM magazines. It collects and places in dialogue 24 articles penned by critics and artists such as La Mamelle / ART COM founder Carl Loeffler, Lynn Hershman, Richard Irwin, Anna Couey, and Linda Montano, plus interviews with artists such as Douglas Davis and Eleanor Antin.<br><br>This collection represents the complexity of the ideas presented in the exhibition as they were grappled with at the time of their original publication, and it also positions them as contemporary questions; particularly relevant is the mediation of performance. It also features conversations between the curators and two of La Mamelle / ART COM\'s key figures, Nancy Frank and Darlene Tong.<br><br>Give Them the Picture is a co-publication between the CCA Wattis Institute for Contemporary Arts and the Graduate Program in Curatorial Practice. Generous support for God Only Knows Who the Audience Is: Performance, Video, and Television Through the Lens of La Mamelle / ART COM has been provided by the Live Oak Foundation and Roger & Victoria Sant. Designed by Jon Sueda / Stripe.<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/god-only-knows-who-audience\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(50,1,'2014-07-27 16:12:33','2020-10-30 21:36:19',348,'More American Photographs\r\n$25',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'more-american-photographs-25',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2011\r\nPaperback, 95 pages\r\n\r\n$25.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=146&SINGLESTORE=true\">Purchase this publication here.</a>',_binary 'As the United States slowly emerges from its most significant economic downturn since the Great Depression, the CCA Wattis Institute reexamined the well-known photography program of the Farm Security Administration, 1935-44, which included artists such as Walker Evans, Ben Shahn, Dorothea Lange and Marion Post Wolcott. For <a href=\'http://archive.wattis.org/exhibitions/more-american-photographs\' target=\'new\'>More American Photographs</a>, twelve contemporary photographers were commissioned to travel the United States, documenting its land and people.<br><br>Featured Artists<br>Walead Beshty, Larry Clark, Roe Ethridge, Katy Grannan, William E. Jones, Sharon Lockhart, Catherine Opie, Martha Rosler, Collier Schorr, Stephen Shore, Alec Soth, Hank Willis Thomas, Esther Bubley, Walker Evans, Dorothea Lange, Russell Lee, Gordon Parks, Marion Post Wolcott, Ben Shahn<br><br><a href=\'http://archive.wattis.org/exhibitions/more-american-photographs\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(51,1,'2014-07-27 16:13:20','2020-10-30 21:37:17',349,'Painting Between the Lines\r\n$15',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'painting-between-the-lines-15',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2011\r\nHardcover, 71 pages\r\n\r\n$15.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=147&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary '<a href=\'http://archive.wattis.org/exhibitions/painting-between-the-lines\' target=\'new\'>Painting Between The Lines</a>, continues the investigation into the relationship between literature and art by commissioning 14 contemporary artists to create paintings based on descriptions of paintings in historical and contemporary novels.<br><br>Artists and novels include:<br><br>Laylah Ali<br>Thomas Mann, The Magic Mountain<br>Michaël Borremans<br>Günter Grass, The Tin Drum<br>Marcel Dzama<br>Haruki Murakami, Kafka on the Shore<br>Cecilia Edefalk<br>August Strindberg, The Red Room<br>Jordan Kantor<br>Albert Camus, Exile and the Kingdom <br>Michael van Ofen<br>Umberto Eco, The Name of the Rose<br>Laura Owens<br>Sylvia Plath, The Bell Jar <br>Clare Rojas<br>Marcel Proust, Swann\'s Way <br>Wilhelm Sasnal<br>Milan Kundera, The Unbearable Lightness of Being <br>Maaike Schoorel<br>Daphne du Maurier, Rebecca<br>Raqib Shaw<br>Fyodor Dostoevsky, The Brothers Karamazov<br>Norbert Schwontkowski<br>Oscar Wilde, The Picture of Dorian Gray<br>Fred Tomaselli<br>Samuel Beckett, Watt <br>Jakub Julian Ziolkowski<br>Sándor Márai, Embers<br><br><a href=\'http://archive.wattis.org/exhibitions/painting-between-the-lines\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(52,1,'2014-07-27 16:14:30','2020-10-30 21:37:49',350,'Huckleberry Finn\r\n$35',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'huckleberry-finn-35',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2010\r\nSoftcover, 108 pages\r\n\r\n$35.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=148&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>\r\n',_binary 'Designed by Jon Sueda/ Stripe, the 108-page catalog is inspired by the 1885 Charles L. Webster and Company first edition of Adventures of Huckleberry Finn. It will include an essay by the scholar and curator Maurice Berger and photographs of the artworks in the <a href=\'http://archive.wattis.org/exhibitions/huckleberry-finn\' target=\'new\'>exhibition</a>, including the new commissions.<br><br>Featured Artists<br>Edgar Arceneaux, Ruth-Marion Baruch, Romare Bearden, Elizabeth Catlett, Claude Clark, Abraham Cruzvillegas, Jamal Cyrus, Emory Douglas, Geoffrey Farmer, Simon Fujiwara, Ellen Gallagher, Felix Gonzalez-Torres, Rodney Graham, David Hammons, Clementine Hunter, Edward W. Kemble, Dorothea Lange, Tim Lee, Henry Lewis, Glenn Ligon, Pare Lorentz, Jason Meadows, Kristen Morgin, Thomas Nast, Kirsten Pieroth, Horace Pippin, Betye Saar, Yinka Shonibare MBE, Allison Smith, Alec Soth, William Desmond Taylor, Hank Willis Thomas, Kara Walker, Andy Warhol<br><br><a href=\'http://archive.wattis.org/exhibitions/huckleberry-finn\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(53,1,'2014-07-27 16:15:19','2020-10-30 21:38:27',351,'Paul McCarthy\'s Low Life Slow Life\r\n$150',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'paul-mccarthy-s-low-life-slow-life-150',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2010\r\nSoftcover in Tide box, 648 pages\r\n$150.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=149&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'Low Life Slow Life—a hefty, 640-page tome covering a two-part <a href=\'http://archive.wattis.org/exhibitions/paul-mccarthys-low-life-slow-life-part-1\' target=\'new\'>exhibition</a> at San Francisco\'s CCA Wattis Institute curated by Los Angeles-based artist Paul McCarthy—is packaged as an instantly recognizable recreation of a Tide box, circa 1973. A fine work of book art in its own right, it showcases a vast range of works that have influenced McCarthy\'s career, presenting a personal map of his individual take on art history alongside his unique creative philosophy.<br><br>This personal map includes works by John Altoon, Gunter Brus, Howard Fried, Dan Graham, Allan Kaprow, Rachel Khedoori, Yves Klein, Tetsumi Kudo, Yayoi Kusama, Maria Lassnig, Robert Mallary, Gustav Metzger, Yoko Ono, Lil Picard, Jason Rhoades, Dieter Roth, Barbara Smith, Stan VanDerBeek and Andy Warhol. The catalogue, which is designed by McCarthy with Jon Sueda / Stripe, also includes an interview with McCarthy and an essay on his work by Wattis Institute curator Jens Hoffmann.<br><br><a href=\'http://archive.wattis.org/exhibitions/paul-mccarthys-low-life-slow-life-part-1\' target=\'new\'>Go to exhibition</a> <br><br><a href=\'catalogs\'><</a>',NULL),(54,1,'2014-07-27 16:15:51','2019-05-20 21:54:52',353,'We have as much time as it takes\r\nSold Out',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'we-have-as-much-time-as-it-takes-sold-out',NULL,NULL,NULL,NULL,NULL,_binary 'Graduate Program in Curatorial Practice at CCA and CCA Wattis Institute for Contemporary Arts, 2010\r\nPaperback, 112 pages\r\nSOLD OUT',_binary 'Designed by Jon Sueda / Stripe, this one-color, 112-page <a href=\'http://archive.wattis.org/exhibitions/we-have-much-time-it-takes\' target=\'new\'>exhibition</a> catalog reflects many of the themes of We have as much time as it takes, including circular processes and resistance to the production of assessable results. The book features an exposed binding, nontraditional typesetting, and the absence of a cover. Challenging a common function of the conventional exhibition catalog, the images are speculative rather than documentary: a set of 10 drawings by Matthew Rana (a student in the Graduate Program in Visual and Critical Studies) illustrates his interpretation of the works in the show prior to the exhibition\'s creation. The book also includes new texts by the poet Jasper Bernes and the writers Erica Levin and Daniel Marcus, an essay by Jacqueline Clay and Kristin Korolowicz, and interviews with each of the featured artists.<br><br>This publication was made possible by the generous support of Jon Sueda / Stripe, San Francisco.<br><br>Featured Artists<br>Nina Beier and Marie Lund, David Horvitz, Jason Mena, Sandra Nakamura, Roman Ondák, Red76, Zachary Royer Scholz, Tercerunquinto, Lawrence Weiner, Christine Wong Yap<br><br><a href=\'http://archive.wattis.org/exhibitions/we-have-much-time-it-takes\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(55,1,'2014-07-27 16:16:34','2020-10-30 21:39:24',352,'Moby Dick\r\n$30',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'moby-dick-30',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2009\r\nHardcover, 136 pages\r\n$30.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=150&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'Designed by Jon Sueda / Stripe, the 136-page exhibition catalog is designed as a homage to the classic 1930 Random House edition of Moby-Dick, with its illustrations by Rockwell Kent, this investigation of present-day America through the lens of Herman Melville\'s great novel convenes artists and filmmakers who have made works relating to its themes of seafaring and the pitting of humankind against the elements. The book also boasts a foil-stamped clothbound cover, a fold-out map, essays by Jens Hoffmann and Alexander Nemerov, and full-color illustrations.<br><br>Featured Artists<br>Kenneth Anger, Matthew Benedict, Mark Bradford, Marcel Broodthaers, Angela Bulloch, Tom Burr, Tacita Dean, Marcel Dzama, Ellen Gallagher with Edgar Cleijne, Felix Gonzalez-Torres, Rodney Graham, John Gutmann, Susan Hiller, Evan Holloway, Peter Hutton, Colter Jacobsen, Brian Jungen, Buster Keaton, Rockwell Kent, Mateo López, Jorge Macchi, Kris Martin, Henrik Olesen, Paulina Olowska, Damián Ortega, Jean Painlevé, Kirsten Pieroth, Richard Serra, Andreas Slominksi, Hiroshi Sugimoto, Adrián Villar Rojas, Orson Welles<br><br><a href=\'http://archive.wattis.org/exhibitions/moby-dick\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(56,1,'2014-07-27 16:17:12','2020-10-30 21:39:56',354,'Amateurs\r\n$30',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'amateurs-30',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2008\r\nPaperback, 112 pages\r\n$30.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=152&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'Placing the featured works in Amateurs in a larger historical context, this fully illustrated catalog is the first of its kind to survey this aesthetic terrain. Given the important and increasingly pervasive practice of the amateur, this publication, which includes essays by Ralph Rugoff and John Roberts, as well as details of all the works in the show , will make a significant contribution to contemporary art discourse.<br><br><a href=\'http://archive.wattis.org/exhibitions/amateurs\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(57,1,'2014-07-27 16:17:42','2020-10-30 21:40:32',355,'Capp Street Project: Beatriz Santiago MuÃ±oz\r\n$12',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'capp-street-project-beatriz-santiago-mua-oz-12',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2008\r\nPaperback, 48 pages\r\n$12.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=153&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'Beatriz Santiago Muñoz\'s Fall 2008 Capp Street Residency is accompanied by a full-color, 48-page catalog, the first-ever monograph on the artist\'s work. It includes new texts by the artist, Julieta González (the Puerto Rico-based curator and writer), and Claire Fitzsimmons (deputy director of the CCA Wattis Institute and curator of this project).<br><br><a href=\'http://archive.wattis.org/exhibitions/capp-street-project-beatriz-santiago-mu%C3%B1oz\' target=\'new\'>More information</a><br><br><a href=\'catalogs\'><</a>',NULL),(58,1,'2014-07-27 16:18:23','2019-05-20 21:55:35',356,'The Exhibition Formerly Known As Passengers\r\nSold Out ',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'the-exhibition-formerly-known-as-passengers-sold-out',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2008\r\nPaperback, 8 pages\r\nSOLD OUT',_binary 'Launched in September 2007, the exhibition Passengers aimed to overcome the often restrictive programming schedule of a typical art institution by implementing a format that was constantly transforming and self-perpetuating. At the beginning of every month, one of the artists from the group portion of the show would move into the central cube to present a solo show, and then at the end of the month he or she would leave the exhibition completely and a brand-new artist would enter the group show, thus constantly repopulating the display.<br><br><a href=\'http://archive.wattis.org/exhibitions/exhibition-formerly-known-passengers\' target=\'new\'>The Exhibition Formerly Known As Passengers</a>, as we are now calling it in its second year, follows largely the same format but discontinues the monthly new addition, so that the show will eventually reach an endpoint—a solo representation of the remaining single artist—in August 2009. Each month the gallery architecture will adapt, with the cube expanding as the group show contracts.<br><br>Each monthly solo exhibition is accompanied by a small booklet featuring a text (often the first published interpretations on one of these emerging artist\'s work) by curator Jens Hoffmann. Featuring images of the works in the show, together this brightly colored series reveal the latest developments in contemporary art practice. Designed by Stripe/Jon Sueda.<br><br>Booklets Available:<br>Tauba Auerbach<br>Kris Martin<br>Dirk Stewen<br>Kirsten Pieroth<br>Colter Jacobsen<br>Jordan Wolfson<br>Kristen Morgin<br>Peter Coffin<br>Abraham Cruzvillegas<br>Claire Fontaine<br>Mario Garcia Torres<br>Aurélien Froment<br>Stowaways<br><br><a href=\'http://archive.wattis.org/exhibitions/exhibition-formerly-known-passengers\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(59,1,'2014-07-27 16:19:04','2019-05-20 21:55:54',357,'The Wizard of Oz\r\nSold Out',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'the-wizard-of-oz-sold-out',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2008\r\nHardcover, 76 pages\r\nSOLD OUT',_binary 'Designed by Jon Sueda / Stripe, the 76-page exhibition catalog is inspired by the first edition of Baum\'s novel. It includes a full-color plate section (including photographs of the new commissions), a text on each participating artist, and critical essays by Jens Hoffmann (curator and director of the Wattis Institute) and Rebecca Loncraine (writer and Baum biographer).<br><br><a href=\'http://archive.wattis.org/exhibitions/wizard-oz\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(60,1,'2014-07-27 16:19:35','2020-10-30 21:41:20',358,'Apocalypse Now The Theater of War\r\n$12',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'apocalypse-now-the-theater-of-war-12',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2007\r\nPaperback, 32 pages\r\n$12.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=156&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'This book includes an interview with Jennifer Allora and Guillermo Calzadilla on their curatorial approach to the exhibition, <a href=\'http://archive.wattis.org/exhibitions/apocalypse-now-theater-war\' target=\'new\'>Apocalypse Now</a>, as well as an introductory essay by curator Jens Hoffmann and an excerpt from Ernst Friedrich\'s seminal text War Against War!. With details of all the featured works in Apocalypse Now, it forms a vital record of this timely exhibition. Designed by Stripe/Jon Sueda.<br><br>Featured Artists<br>Antonin Artaud, Max Beckmann, Margaret Bourke-White, Mathew Brady, Jacques Callot, Bruce Conner, Leonardo da Vinci, Otto Dix, Ernst Friedrich, Francisco de Goya, George Grosz, John Heartfield, Ernst Jünger, Jon Kessler, Käthe Kollwitz, Lewis Milestone, Bruce Nauman, Pino Pascali, Pablo Picasso, Alain Resnais, Alexander Rodchenko, Martha Rosler, Luigi Russolo, Kurt Schwitters, Richard Serra, Mark Twain<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/apocalypse-now-theater-war\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(61,1,'2014-07-27 16:20:16','2020-10-30 21:41:54',359,'Capp Street Project: Mario Ybarra Jr.\r\n$12',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'capp-street-project-mario-ybarra-jr-12',NULL,NULL,'2014-07-27 20:07:00',NULL,NULL,_binary 'CCA Wattis Institute, 2007\r\nPaperback, 48 pages\r\n$12.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=157&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'This catalog is the first-ever monograph on Mario Ybarra Jr.\'s work and an important document of the mural he produced at the Wattis Institute during his Capp Street Project residency. It includes full-color installation photographs, images of his previous works, a tour given by the artist of some of San Francisco\'s most historically important murals, a text by Claire Fitzsimmons on his Wattis Institute mural, and an essay by Jens Hoffmann surveying his entire oeuvre. Designed by STRIPE / Jon Sueda.<br><br><a href=\'http://archive.wattis.org/exhibitions/capp-street-project-mario-ybarra-jr\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(62,1,'2014-07-27 16:20:55','2019-05-20 21:56:58',360,'Passengers\r\nSold Out',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'passengers-sold-out',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2007\r\nPaperback, 8 pages\r\nSOLD OUT',_binary 'Passengers is a permanent but constantly transforming <a href=\'http://archive.wattis.org/exhibitions/passengers\' target=\'new\'>exhibition</a> of emerging international contemporary artists. The Wattis Upper gallery has been divided into two separate spaces, one of which features a group show of 11 artists, and the other a solo exhibition. At the end of each month, the solo artist will leave the exhibition completely, an artist from the group show will move into the solo space, and a new artist will be introduced into the group show.<br><br>Each monthly solo exhibition is accompanied by a small booklet featuring a text (often the first published interpretations on one of these emerging artist\'s work) by curator Jens Hoffmann. Featuring images of the works in the show, together this brightly colored series reveal the latest developments in contemporary art practice. Designed by Stripe/Jon Sueda.<br><br>Booklets Available:<br>Daria Martin<br>Alexandre Da Cunha<br>Ryan Gander<br>Shana Lutker<br>Tim Lee (sold out)<br>Annette Kelm<br>João Maria Gusmão + Pedro Paiva<br>Ulla Von Brandenburg<br>Gareth Moore<br>Roman Ondák<br>Valérie Mréjen<br>Federico Herrero<br><br><a href=\'http://archive.wattis.org/exhibitions/passengers\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(63,1,'2014-07-27 16:21:34','2020-10-30 21:42:36',361,'Pioneers\r\n$12',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'pioneers-12',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2007\r\nPaperback, 24 pages\r\n$12.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=159&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'Pioneers draws an analogy between the achievements of pioneering artists from the last 100 years and the defining character of the nineteenth-century California pioneers. This booklet includes a text by exhibition curator Jens Hoffmann, a historical survey of San Francisco\'s early origins, and texts on each of the 20 featured artists. Designed by STRIPE / Jon Sueda.<br><br>Featured Artists<br>Ant Farm, Robert Bechtle, Wallace Berman, The Cockettes, Bruce Conner,Jay DeFeo, Emory Douglas, Yun Gee, Anna Halprin, Lynn Hershman, Leeson,Jess, Paul Kos, Dorothea Lange, José Montoya, Diego Rivera, Achilles Rizzoli, Ed Rossbach, Mario Savio, Rosie Lee Tompkins, Katherine Westphal<br><br><a href=\'http://archive.wattis.org/exhibitions/pioneers\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(64,1,'2014-07-27 16:22:11','2020-10-30 21:43:12',362,'Tim Lee\r\n$16',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'tim-lee-16',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2007\r\nPaperback, 88 pages\r\n$16.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=160&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'To accompany Tim Lee\'s Capp Street Project, this monograph includes two texts written by the artist over the course of his residency on the musician Neil Young and the comedian Steve Martin. This publication also includes an essay by Jens Hoffmann on the relationship between art and comedy and a text by Claire Fitzsimmons on the works produced over the course of Lee\'s residency, with illustrations of the new commissions for the Wattis Institute. Designed by Stripe/Jon Sueda.<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/capp-street-project-tim-lee\' target=\'new\'>More information</a><br><br><a href=\'catalogs\'><</a>',NULL),(65,1,'2014-07-27 16:22:51','2020-10-30 21:45:46',363,'A Brief History of Invisible Art\r\n$20',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'a-brief-history-of-invisible-art-20',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2006\r\nPaperback, 65 pages\r\n$20.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=161&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'A Brief History of Invisible Art is a fully illustrated catalog with essay by Ralph Rugoff, which brings together artworks from six decades that place a pronounced emphasis on the conceptual and communicative possibilities of the work of art, while bypassing its seeming requirements of visibility and materiality. In surveying this terrain, the exhibition includes works that represent a wide range of aesthetic practices and that engage with surprisingly diverse concerns. Whether underscoring the role of the audience, mocking the theological aura of museum rhetoric or calling attention to the importance of linguistic description in cultural production, these works prompt us to see through the more grandiose distractions of contemporary art and so to think more clearly about its underlying functions.<br><br>Featured Artists<br>Art & Language, Michael Asher, Robert Barry, James Lee Byars, Maurizio Cattelan, Jay Chung, Trisha Donnelly, Tom Friedman, Carsten Höller, Bethan Huws, Bruno Jakob, Yves Klein, Glenn Ligon, Jonathan Monk, Gianni Motti, Andy Warhol<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/brief-history-invisible-art\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(66,1,'2014-07-27 16:23:26','2020-10-30 21:47:21',364,'Prophets of Deceit\r\n$18',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'prophets-of-deceit-18',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2006\r\nPaperback, 60 pages\r\n$18.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=164&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'Prophets of Deceit examines messianic and apocalyptic cults, predictions, and prophecies as systems of restraining social behavior and how those systems ultimately influence the outcome of history. The exhibition considers the degree in which prophetic guidelines--set either by powerful individuals, extreme religions or unexpected events--play in shaping modern economic, cultural, political, and social frameworks.<br><br>Featured Artists<br>Craig Baldwin, Tacita Dean, Rod Dickinson, HCRH, Christian Jankowski, Joachim Koester, Komar & Melamid, John Menick, Melvin Moti, Raymond Pettibon, Mungo Thomson, Carl Michael von Hausswolff, Erik Pauser<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/prophets-deceit\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(67,1,'2014-07-27 16:24:06','2020-10-30 21:47:51',365,'Monuments for the USA\r\n$30',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'monuments-for-the-usa-30',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2005\r\nPaperback, 160 pages\r\n$30.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=165&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'With an essay by Ralph Rugoff, individual contributions by all participating artists, and over 70 full color illustrations.<br><br>This publication brings together the proposals for monuments for the United States of America by the over 60 international artists invited to participate in the attendant exhibition. Freed from contextual, budgetary, or practical constraints, the proposals reflect each artist\'s ideas about the type of monument the people of the United States currently need or deserve, and reexamine our notions of what monuments can be, and what role they can play in our civic and imaginative life.<br><br>Featured Artists<br>Allora & Calzadilla, Tariq Alvi, Janine Antoni, Edgar Arceneaux, Artemio, Robert Beck, Michel Blazy, Monica Bonvicini, Andrea Bowers, Fernando Bryce, Los Carpinteros, Paul Chan, Adam Chodzko, Martin Creed, Enrico David, Jeremy Deller, Thomas Demand, Jessica Diamond, Sam Durant, Shannon Ebner, Elmgreen & Dragset, Meschac Gaba, Anya Gallaccio, Hans Haacke, Susan Hiller, Thomas Hirschhorn, Chris Johanson & Kal Spelletich, Michael Joo, Ilya & Emilia Kabakov, Brad Kahlhamer, Barbara Kruger, Gabriel Kuri, Ken Lum, Jason Meadows, Aleksandra Mir, Liliana Moro, Mike Nelson, Paul Noble, Yoshua Okon, Jennifer Pastor, Kiersten Pieroth, Paola Pivi, Marjetica Potrc, Tobias Putrih, Qiu Zhijie, Rigo 23, Matthew Ronay, Michael Ross, Santiago Sierra, Gary Simmons, Yutaka Sone, Frances Stark, Michael Stevenson, Do-Ho Suh, Torolab, Shirley Tse, Jeffrey Vallance, Mark Wallinger, Olav Westphalen, Xu Zhen, Zhang Huan<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/monuments-usa\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(68,1,'2014-07-27 16:24:37','2020-10-30 21:48:22',366,'Thomas Hirschhorn Utopia, Utopia = One World, One War, One Army, One Dress\r\n$20',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'thomas-hirschhorn-utopia-utopia-one-world-one-war-one-army-one-dress-20',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute and the Institute of Contemporary Art, Boston, 2005\r\nPaperback, 52 pages\r\n$20.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=166&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'A publication designed in collaboration with the artist documenting the exhibition, which includes writings about camouflage, subjectivity and war. Essays by Nicholas Baume & Ralph Rugoff, Pamela M. Lee, Thomas Hirschhorn, and Marcus Steinweg.<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/thomas-hirschhorn\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(69,1,'2014-07-27 16:25:11','2019-05-20 21:59:49',367,'What We Want Is Free: Generosity and Exchange in Recent Art\r\nSold Out',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'what-we-want-is-free-generosity-and-exchange-in-recent-art-sold-out',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute/State University of New York Press, 2005\r\nPaperback, 184 pages\r\nSOLD OUT',_binary 'Edited by Ted Purves, with essays by Bill Arning, Kate Fowle, Jeanne van Heeswijk, Mary Jane Jacob, Ben Kinmont, Lars Bang Larsen, Ted Purves, Jörgen Svensson.<br><br>Arising from a series of artists\' projects and a symposium organized by the Wattis Institute, this book examines contemporary artists\' use of the \"gift\"—the distribution of goods and services—as a medium for artistic production.<br><br>Featuring a detailed survey of over 50 artists\' projects from 15 countries, What We Want Is Free explores how these artists use their projects to connect participants to tangible goods and services that they might need, enjoy, and benefit from.<br><br>Featured Artists<br>Jeremy Deller, Harrell Fletcher & Jon Rubin, Felix Gonzalez-Torres, Wang Jin, Ben Kinmont, Suzanne Lacy and Pilar Riano, Mierle Laderman Ukeles, Cesare Pietroiusti, Michael Rakowitz, Temporary Services, Simparch, Superflex, Rirkrit Tiravanija, W. A. Ehren Tool<br><br><a href=\'catalogs\'><</a>',NULL),(70,1,'2014-07-27 16:25:41','2019-05-20 22:00:21',368,'Irreducible Contemporary Short Form Video\r\nSold Out',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'irreducible-contemporary-short-form-video-sold-out',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2004\r\nPaperback, 44 pages\r\nSOLD OUT',_binary 'Featuring artists from approximately 20 different countries, this international survey and its accompanying catalog bring together recent video works that are structured around a single situation, action, or individual.<br><br>With an introductory essay by Ralph Rugoff.<br><br>Featured Artists<br>Dan Acostioaei and Ann Wodinski, Mauricio Alejo, Jennifer Allora and Guillermo Calzadilla, Jesper Alvaer, Yael Bartana, Mircea Cantor, Douglas Gordon, David Hammons, Kimsooja, Glenda Leon, Mark Lewis, Mads Lynnerup, Euan Macdonald, Aernout Mik, Will Rogan, Aïda Ruilova, Anri Sala, Song Dong, Su-Mei Tse, Gillian Wearing, Wood & Harrison, David Zink Yi, Artur Zmijewski<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/irreducible\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(71,1,'2014-07-27 16:26:07','2020-10-30 21:49:41',369,'Baja to Vancouver The West Coast and Contemporary Art\r\n$28',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'baja-to-vancouver-the-west-coast-and-contemporary-art-28',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute; Museum of Contemporary Art, San Diego; Seattle Art Museum; Vancouver Art Gallery, 2003\r\nPaperback, 152 pages\r\n$28.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=170&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'The first major survey of recent art from the western edge of the United States, Canada, and Baja California, Mexico. Focusing on works that engage with the social landscapes of this transnational corridor, this catalog presents compelling and surprising images of a distinctive cultural terrain—one that is arguably North America\'s most vital region of contemporary art production.<br><br>With essays by Douglas Coupland, Matthew Coolidge of the Center for Land Use Interpretation, Lisa Robertson of the Office for Soft Architecture, Wattis Institute director Ralph Rugoff, and Matthew Stadler and more than 150 full-color reproductions.<br><br>Featured Artists<br>Michael Brophy, Delia Brown, Brian Calvin, Russell Crotty, Trisha Donnelly, Stan Douglas, Roman de Salvo, Sam Durant, Thomas Eggerer, Kota Ezawa, Harrell Fletcher and Miranda July, Evan Holloway, Chris Johansen, Brian Jungen, Tim Lee, Ken Lum, Liz Magor, Matt McCormick, Roy McMakin, Mark Mumford, Shannon Oksanen / Scott Livingstone, Michelle O\'Marah, Marcos Ramirez, Glenn Rudolph, Steven Shearer, Catherine Sullivan, Larry Sultan, Ron Terada, Althea Thauberger, Torolab (Raul Cardenas, Marcela Guadiana de Cardenas, et al.), Yvonne Venegas<br><br><a href=\'http://archive.wattis.org/exhibitions/baja-vancouver-west-coast-and-contemporary-art\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(72,1,'2014-07-27 16:26:47','2020-10-30 21:50:09',370,'Warped Space\r\n$15',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'warped-space-15',NULL,NULL,NULL,NULL,NULL,_binary 'CCA Wattis Institute, 2003\r\nComic book format, 16 pages\r\n$15.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=177&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>\r\n',_binary 'Presents artworks that generate hypothetical spaces in a high-impact, visually stimulating format. Putting the hex on the clarity and legibility of that old euclidean model, the publication invites you into unpredictable spaces that fluctuate, dissolve, or multiply, often altering your sense of the surrounding architecture. Using different means and media, each of the featured works engineers a psychological, as well as perceptual, disequilibrium, addressing you from a point where your \"objective\" seeing meets hallucinatory vision.<br><br>With text by Ralph Rugoff, Wattis Institute director.<br><br>Featured Artists<br>Ricci Albenda, Jesse Bransford, Rosana Castrillo Diaz, Olafur Eliasson, Graham Gussin, Carsten Höller, Evan Holloway, Rachel Khedoori, D\'Nell Larson, Ann Lislegaard, Josiah McElheny, Yunhee Min, Sandeep Mukherjee, Jennifer Steinkamp, Philip Taaffe, Grazia Toderi, Fred Tomaselli<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/warped-space\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(73,1,'2014-07-27 16:27:21','2020-10-30 21:50:39',371,'How Extraordinary that the World Exists!\r\n$12',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'how-extraordinary-that-the-world-exists-12',NULL,NULL,NULL,NULL,NULL,_binary 'CCAC, 2002\r\nHardback, 57 pages\r\n$12.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=171&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary '57 pages, 9 b/w illustrations, 9 color plates<br>Two books in one! This volume includes Sudden Glory: Sight Gags and Slapstick in Contemporary Art<br>Book design: Aufuldish & Warinner<br><br>The artists in How Extraordinary that the World Exists! explore remarkable aspects of ordinary existence, using real-life subjects as a starting point.<br><br>With an introduction by Ralph Rugoff, director of the CCAC Wattis Institute; an essay by David Spalding; artist biographies; and a list of works in the exhibition.<br><br>Featured Artists<br>Rachel Berwick, Trisha Donnelly,Veli Granö, Daniel Guzmán, Gavin Hipkins, Mary Lucier, Boris Mikhailov, Aleksandra Mir<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'catalogs\'><</a>',NULL),(74,1,'2014-07-27 16:27:50','2019-05-20 22:02:21',372,'Reality Check: Painting in the Exploded Field (Selections from the Vicki and Kent Logan Collection)\r\nSold Out',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'reality-check-painting-in-the-exploded-field-selections-from-the-vicki-and-kent-logan-collection-sold-out',NULL,NULL,NULL,NULL,NULL,_binary 'CCAC, 2002\r\nPaperback, 48 pages\r\nSOLD OUT',_binary '48 pages, 18 color plates<br>Book design: Sara Cambridge<br>AIGA award winner, \"One of the 50 best books of 2002–3\"<br><br>Reality Check: Painting in the Exploded Field presents works from the Vicki and Kent Logan Collection that test both the limits and limitations of painting in a digital age. Infected by the ever-changing surfaces of advertising, album graphics, computer games, and animated cartoons, they engage in a high-energy semiotic sampling. Freely associating between abstraction and representation, the conscious and the unconscious, fantasy and reality, these works ultimately celebrate (and challenge) the persistent dilemma that is painting.<br><br>With an essay by Matthew Higgs, curator of the CCA Wattis Institute; artist biographies; and a list of works in the exhibition.<br><br>Featured Artists<br>Franz Ackermann, Michael Bevilacqua, Chris Finley, Matthew Ritchie, Michel Majerus, Jonathan Seliger, Takashi Murakami<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/reality-check\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(75,1,'2014-07-27 16:28:26','2020-10-30 21:56:50',372,'Rock My World: Recent Art and the Memory of Rock \'n\' Roll\r\n$15',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'rock-my-world-recent-art-and-the-memory-of-rock-n-roll-15',NULL,NULL,NULL,NULL,NULL,_binary 'CCAC, 2002\r\nHardback, 95 pages\r\n$15.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=173&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'CCAC, 2002<br>Hardcover and paperback, 5\" x 7\"<br>95 pages, 24 b/w illustrations, 15 color plates<br>Book design: Volume<br>$8.00 hardcover, $5.00 paperback<br><br>Rock My World: Recent Art and the Memory of Rock \'n\' Roll surveys new art that draws on rock music as a critical reference point.<br><br>The catalog includes three essays:<br><br>\"Untimely Meditations\" by Ralph Rugoff, director of the CCAC Wattis Institute<br>\"Time Out of Mind: Video Art, Romanticism, and Radical Nostalgia\" by Ann Powers, former pop critic at the New York Times and coeditor of Rock She Wrote: Women Write about Rock, Pop, and Rap<br>\"Mutations: Some Thoughts on the Work of Jeremy Deller, Martin Creed, and Margaret Thatcher\" by Matthew Higgs, curator for art and design at the CCAC Wattis Institute<br><br>With artist biographies and a list of works in the exhibition.<br><br>Featured Artists<br>Jessica Bronson, Martin Creed, Jeremy Deller, Sam Durant, Rodney Graham, Evan Holloway, Christian Marclay, Erik Parker, Dario Robleto, Marina Rosenfeld, Steven Shearer, Frances Stark, Mungo Thomson<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/rock-my-world\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(76,1,'2014-07-27 16:28:56','2020-10-30 21:50:58',373,'Sudden Glory: Sight Gags and Slapstick in Contemporary Art & How Extraordinary that the World Exists\r\n$12',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'sudden-glory-sight-gags-and-slapstick-in-contemporary-art-how-extraordinary-that-the-world-exists-12',NULL,NULL,NULL,NULL,NULL,_binary 'CCAC, 2002\r\nSoftcover, 61 pages\r\n$12.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=171&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary '61-page softcover, 4 b/w illustrations, 13 color plates.<br>Two books in one! This volume includes Sudden Glory: Sight Gags and Slapstick in Contemporary Art & How Extraordinary that the World Exists<br>52-page paperback, 8 b/w illustrations, 14 plates (with stickers to be applied by the reader)<br>Book design: Aufuldish & Warinner<br><br>With an essay, \"Falling with Style,\" by Ralph Rugoff, director of the CCAC Wattis Institute; artist biographies; and a list of works in the exhibition.<br><br>Featured Artists<br>Ingar Dragset and Michael Elmgreen, Gelatin, Martin Kersels, Jim Lambie, Steve McQueen, Helen Mirra, Hirsch Perlman, John Pilson, Matthew Ronay, David Shrigley, Roman Signer, Marijke van Warmerdam, John Wood and Paul Harrison<br><br><a href=\'http://archive.wattis.org/exhibitions/sudden-glory-sight-gags-and-slapstick-contemporary-art\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(77,1,'2014-07-27 16:29:51','2019-05-20 22:03:15',374,'Extra Art: A Survey of Artists\' Ephemera, 1960-1999\r\nSold Out',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'extra-art-a-survey-of-artists-ephemera-1960-1999-sold-out',NULL,NULL,NULL,NULL,NULL,_binary 'Smart Art Press, 2001\r\nHardcover, 190 pages\r\nSOLD OUT',_binary 'Book design: Michele Perez<br><br>Extra Art: A Survey of Artists\' Ephemera, 1960–1999 surveys the neglected and important terrain of artists\' ephemera, a category that includes exhibition invitations, posters, ads, stickers, buttons, and other miscellany. The catalog documents a remarkable diversity of works by approximately 190 artists from around the world.<br><br>The catalog includes three essays:<br><br>\"Art for the Occasion/Art de Circonstance\" by Anne Moeglin-Delcroix<br>\"Extra Art: Surveying Artists\' Ephemera, 1960–1999\" by Steven Leiber and Todd Alden<br>\"Tickets, Trips, and Passports: Thoughts About Travel, Souvenirs, and Contemporary Art\" by Ted Purves<br><br>With an introduction by Ralph Rugoff, director of the CCAC Wattis Institute; selected bibliography; and an illustrated list of the more than 1,500 works in the exhibition.<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/extra-art-survey-artists-ephemera-1960-1999\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(78,1,'2014-07-27 16:30:41','2020-10-30 21:57:57',376,'The Artist\'s World\r\n$12',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'the-artist-s-world-12',NULL,NULL,NULL,NULL,NULL,_binary 'CCAC, 2001\r\nHardback, 53 pages\r\n$12.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=176&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'Hardcover and paperback, 5\" x 7\"<br>53 pages, 4 b/w illustrations, 11 plates<br>Two books in one! This volume includes Utopia Now!<br>Book design: Aufuldish & Warinner<br><br>The Artist\'s World comically explores the predicaments faced by contemporary artists, as well as the institutionalized myths and personas that often distort our perception of the art world.<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/artists-world\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(79,1,'2014-07-27 16:31:50','2020-10-30 21:58:09',377,'Utopia Now!\r\n$12',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'utopia-now-12',NULL,NULL,NULL,NULL,NULL,_binary 'CCAC, 2001\r\nHardcover, 53 pages\r\n$12.00\r\n\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=176&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'Hardcover and paperback, 5\" x 7\"<br>53 pages, 4 b/w illustrations, 11 plates<br>Two books in one! This volume includes The Artist\'s World<br>Book design: Aufuldish & Warinner<br>$12.00<br><br>Utopia Now! offers visions of the world as it might be, by artists who also confront the world as it is.<br><br>With an introduction by Marina McDougall, former curator of art and design at the CCAC Wattis Institute; an essay, \"Paradise Recycled,\" by cultural critic Mark Dery, author of Escape Velocity: Cyberculture at the End of the Century and The Pyrotechnic Insanitarium: American Culture on the Brink; an overview of projects in the exhibition; artist biographies; and a list of works in the exhibition.<br><br>Featured Artists<br>Vito Acconci/Acconci Studio, Shigeru Ban, Santiago Cirugeda, Crimson, Amy Franceschini, Chad McCail, Nils Norman, Raketa, Michael Rakowitz, Superflex, Torolab<br><br><a href=\'http://archive.wattis.org/exhibitions/utopia-now\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(80,1,'2014-07-27 16:32:54','2019-05-20 22:04:26',378,'Potent/Present: Selections from the Vicki and Kent Logan Collection\r\nSold Out',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'potent-present-selections-from-the-vicki-and-kent-logan-collection-sold-out',NULL,NULL,NULL,NULL,NULL,_binary 'CCAC, 2000\r\nPaperback, 18 pages\r\nSOLD OUT',_binary 'Paperback, 13\" x 9.25\"<br>18 pages including 13 color plates on fold-out pages<br>Book design: Tenazas Design<br><br>Potent/Present: Selections from the Vicki and Kent Logan Collection represents the West Coast debut of works of photography and sculpture by both emerging and established artists. The exhibition was organized by curator Cristóbal Pérez.<br><br>With artist biographies and a list of works in the exhibition.<br><br>Featured Artists<br>Matt Collinshaw, Keith Edmier, Tracey Emin, Robert Gober, Oliver Herring, Dana Hoey, Rut Blees Luxemburg, Juan Muñoz, Liza May Post, Sam Taylor-Wood, Rachel Whiteread, David Wojnarowicz, Zhang Huan<br><br><a href=\'http://archive.wattis.org/exhibitions/potentpresent\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(81,1,'2014-07-27 16:34:21','2020-10-30 21:58:47',379,'Searchlight: Consciousness at the Millennium\r\n$28',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'searchlight-consciousness-at-the-millennium-28',NULL,NULL,NULL,NULL,NULL,_binary 'Thames & Hudson, 1999\r\nPaperback, 194 pages\r\n$28.00\r\n\r\n<a href=\"https://secure.touchnet.net/C20080_ustores/web/product_detail.jsp?PRODUCTID=179&SINGLESTORE=true\" target=“_blank”>Purchase this publication here.</a>',_binary 'Paperback, 9.07\" x 11.27\"<br>194 pages, 357 illustrations, 300 in color<br>Book design: Appetite Engineers: Martin Venezky and Geoff Kaplan<br><br>Searchlight: Consciousness at the Millennium is an unprecedented exploration of the nature of consciousness and its embodiment in many forms of contemporary art, including installation, painting, sculpture, video, film, and computer media.<br><br>The catalog includes a comprehensive essay by Lawrence Rinder, former director of the CCAC Wattis Institute, and George Lakoff, professor of linguistics at UC Berkeley; pieces by writers in the fields of art history, philosophy, cognitive science, theology, and linguistics; and a list of works in the exhibition.<br><br>Featured Artists<br>Lutz Bacher, Robert Barry, Samuel Beckett, Louise Bourgeois, Theresa Hak Kyung Cha, Martin Creed, Cristabel Davé, Stan Douglas, Douglas Gordon, Rodney Graham, David Hannah, Jörg Herold, Gary Hill, Robert Irwin, Paul Kaiser and Shelley Eshkar, Agnes Martin, Yoko Ono, Kristin Oppenheim, Adrian Piper, Markus Raetz, Ad Reinhardt, Stuart Sherman, Imogen Stidworthy, Diana Thater, Rosie Lee Tompkins, Bill Viola, Gillian Wearing, Pascale Wiedemann, La Monte Young and Marian Zazeela<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/searchlightconsciousness-millennium\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(82,1,'2014-07-27 16:34:55','2019-05-20 22:05:24',380,'Spaced Out: Late 1990s Works from the Vicki and Kent Logan Collection\r\nSold Out',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'spaced-out-late-1990s-works-from-the-vicki-and-kent-logan-collection-sold-out',NULL,NULL,NULL,NULL,NULL,_binary 'CCAC, 1999\r\nPaperback, 50 pages\r\nSOLD OUT',_binary 'Paperback, 5.75\" x 7.5\"<br>50 pages, 12 color plates<br>Book design: Morla Design<br><br>The varied works in Spaced Out: Late 1990s Works from the Vicki and Kent Logan Collection explore aspects of the body in space and the body as a vehicle for consciousness.<br><br>With an essay by Lawrence Rinder, former director of the CCAC Wattis Institute; artist biographies; and a list of works in the exhibition.<br><br>Featured Artists<br>Thomas Demand, Marlene Dumas, Antony Gormley, Guo Wei, Glenn Ligon, Sharon Lockhart, Ron Mueck, Bruce Nauman, Liza May Post, Marc Quinn, Cindy Sherman, Nicola Tyson<br><br>There are a limited number of copies of this publication left.<br><br><a href=\'http://archive.wattis.org/exhibitions/spaced-out\' target=\'new\'>Go to exhibition</a><br><br><a href=\'catalogs\'><</a>',NULL),(83,1,'2014-07-28 00:17:30','2021-06-10 16:50:18',120,'Newsletter',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'83',NULL,NULL,NULL,NULL,NULL,_binary '\r\n																								',_binary '\r\n																								<a href=\"http://visitor.r20.constantcontact.com/d.jsp?llr=p5q4x4qab&amp;p=oi&amp;m=1117756834767&amp;sit=fmbork8ib&amp;f=697249c3-8765-4e88-af24-28ce4c573ca9\" target=\"new\">Subscribe</a> to our newsletter . . .',_binary '\r\n																								'),(84,1,'2014-07-29 17:36:57','2019-06-19 01:08:59',98,'Nairy Baghramian: <i>Off Broadway</i> (2015)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'nairy-baghramian-off-broadway-2015',NULL,'2015-01-21 00:00:00','2015-02-28 00:00:00',NULL,NULL,'',_binary 'After spending the fall in San Francisco, <a href=\"\">Nairy Baghramian</a> presents <i>Off Broadway</i> <br><br>With works, props, and lectures by:<i> Rhea Anastas, Katarina Burin, Michaela Eichwald, Vincent Fecteau/Diane Simpson/==, Frieda Grafe/Filmkritik, Margaret Harrison, Tom Jimmerson, Tobias Kaspar, Heinz-Peter Knes, Janette Laverrière, Adam Linder, Shahryar Nashat, Paulina Olowska, PROVENCE, Phel Steinmetz, Danh Vo, </i>and <i>Michaela Wünsch.</i><br><br>A central idea for Nairy Baghramian\'s exhibition is a consideration of the nature of the prop.  Her departure point is a scene from <i>Les Demoiselles de Rochefort</i>, a 1967 musical film by Jacques Demy, starring Catherine Deneuve and Gene Kelly. In several scenes of the film, Demy uses artworks as props to help build his characters and their relationships to the world.  Instead of being independent works of art, the objects perform cameos or stand-ins for the film’s plot, such as a portrait of an “ideal woman.”<br><br><i>The starting point for the project is to pose two questions. One is to examine how the perception of a critical failure or a lack of success—or the identification of problems—can actually ignite closer interest. The second begins with the observation that contemporary visual art routinely leverages other disciplines such as literature, dance, film, magazines, fashion, interior design, architecture to add value and extend the notion of art. But what if this cultural relation is reciprocal: what is the status and efficacy of art moving in the other direction, or playing a supporting role such as in </i>Les Demoiselles<i>? What if an exhibition embraces the notion of art performing a supporting role?</i> (Nairy Baghramian)<br><br>To address these questions, Baghramian splits the gallery in two: on one side is a traditional white wall, and the other side is “storage.” One by one, each work is displayed on the white wall for a short time: they maintain their autonomy as works of art and assert themselves as the protagonists of their own story. Each work then joins the others in storage and becomes another “prop,” ready to serve a supporting role in someone else’s story.   (AH)<br><br><i>This residency and exhibition are made possible by Institut für Auslandsbeziehungen, the Goethe-Institut San Francisco, and Galerie Daniel Buchholz, with special thanks to Silberkuppe</i>.<br><br>Nairy Baghramian invited by Anthony Huberman.<br><br>Press:<br><br><a href=\'http://www.wattis.org/media/00891.pdf\' target=\'new\'><i>ArtBusiness</i></a><br><a href=\'http://www.wattis.org/media/00892.pdf\' target=\'new\'><i>Contemporary Art Daily</i></a>',NULL),(85,1,'2014-07-29 17:41:59','2021-06-10 16:33:01',NULL,'Joan Jonas is on our mind (2014-2015)',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'joan-jonas-is-on-our-mind-2014-2015',NULL,'2014-09-09 00:00:00','2015-06-30 00:00:00',NULL,NULL,_binary '\r\n																								<i>A year-long season of public events and conversation.</i><br><br><div>(Learn more about our research seasons&nbsp;<a href=\"/read-about-us/the-research-seasons\">here</a>.)</div>\r\n																								',_binary '<a href=\"\">Joan Jonas</a> is on our mind.<br><div><br>[image1]<br> Introduction<br>by Anthony Huberman<br><br>“De-synchronization, usually in conjunction with fragmentation and repetition” was Douglas Crimp’s crisp and eloquent summary of her work. Jonas cuts up and displaces elements from dance, theater, myth, landscape, and media to tell abstract stories. The parts of the whole are the parts of the piece—the projection, the performer, the set, the sound, the ritual. <br><br>“I explore the place of women in history as outsiders—healers—witches—storytellers,” she says.<br><br>Jonas is a pioneer of video and performance art; her early work from the 1960s and 1970s anticipated the following forty years of multimedia. <br><br>*<br><br>From September 2014 to June 2015:<br><br>We heard a lecture by <a href=\"/calendar/saturday-september-13-2014\">Ann Reynolds</a> on Joan Jonas. We hosted a lecture by <a href=\"/calendar/thursday-october-9-2014\">Joan Jonas</a>. <a href=\"/calendar/monday-november-17-2014\">Prof. Adrian Daub</a> talked about the concept of repetition. Poet <a href=\"/calendar/monday-december-8-2014\">Ronaldo V. Wilson</a> did a reading. We screened films and had a conversation with <a href=\"/calendar/monday-february-9-2015\">Loretta Fahrenholz</a>. <a href=\"/calendar/monday-march-9-2015\">Olivia Mole</a> presented a performance. We invited <a href=\"/calendar/thursday-march-19-friday-march-20-2015\">Charlemagne Palestine</a> to screen films and perform on piano. <a href=\"/calendar/monday-april-13-2015\">Doug Hall</a> gave a talk. <a href=\"/calendar/monday-june-1-2015\"> Paul Sietsema</a> screened a recent 16mm film.<br><br>Click on the names above to read more about each one and to see/hear documentation. <br><br>*<br><br>We published <a href=\"/on-our-mind/joan-jonas-is-on-our-mind-2014-2015/jonas-and-her-precursors-by-jordan-kantor\"> Jordan Kantor</a> on Joan Jonas; <a href=\"/on-our-mind/joan-jonas-is-on-our-mind-2014-2015/478\"> Patricia Maloney</a> on Joan Jonas; <a href=\"/on-our-mind/joan-jonas-is-on-our-mind-2014-2015/a-oeno-art-is-beneath-your-powera-joan-jonas-dancing-on-a-wall-by-frances-richard\">Frances Richard</a> on Joan Jonas; <a href=\"/on-our-mind/joan-jonas-is-on-our-mind-2014-2015/the-recorded-instrument-by-jamie-stevens\">Jamie Stevens</a> on Joan Jonas; <a href=\"/on-our-mind/joan-jonas-is-on-our-mind-2014-2015/epilogue-yogaflogogo-by-olivia-mole-ranu-mukherjee-and-jacqueline-francis\">Olivia Mole, Ranu Mukherjee, and Jacqueline Francis</a> on Joan Jonas; <a href=\"/on-our-mind/joan-jonas-is-on-our-mind-2014-2015/the-first-time-i-became-another-by-susan-gevirtz-jeanne-c-finley\"> Susan Gevirtz and Jeanne C. Finley</a> on Joan Jonas; and <a href=\"/on-our-mind/joan-jonas-is-on-our-mind-2014-2015/drawing-joan-jonas-material-or-immaterial-by-elizabeth-mangini\"> Elizabeth Mangini</a> on Joan Jonas.<br><br>*<br><br>A publication is available <a href=\"/buy-catalogues/joan-jonas-is-on-our-mind-15\">here</a>.<br><br>*<br><br>The reading group included Jeanne C. Finley, Jacqueline Francis, Susan Gevirtz, Anthony Huberman, Jordan Kantor, Tirza T. Latimer, Patricia Maloney, Elizabeth Mangini, Ranu Mukherjee, Frances Richard, Jordana Moore Saggese, and Jamie Stevens, with research assistance provided by Kathryn Crocker.<br><br>With special thanks to Joan  Jonas.<br></div>',_binary '\r\n																								\r\n																								<iframe width=\"500\" height=\"281\" src=\"//www.youtube.com/embed/cpfJ3gPr3j0\" frameborder=\"0\" allowfullscreen=\"\"></iframe>\r\n\r\n<object width=\"500\" height=\"281\">\r\n<param name=\"src\" value=\"http://ubumexico.centro.org.mx/video/Le-Grice-Malcolm_Berlin-Horse_1970.mp4\">\r\n<param name=\"autoplay\" value=\"false\">\r\n<embed src=\"http://ubumexico.centro.org.mx/video/Le-Grice-Malcolm_Berlin-Horse_1970.mp4\" type=\"video/quicktime\" width=\"200\" height=\"50\" autoplay=\"false\">\r\n</object>\r\n\r\n<object width=\"500\" height=\"281\">\r\n<param name=\"src\" value=\"http://criterion-trailers.s3.amazonaws.com/EYES_WITHOUT_A_FACE_trailer_x500.mp4\">\r\n<param name=\"autoplay\" value=\"false\">\r\n<embed src=\"http://criterion-trailers.s3.amazonaws.com/EYES_WITHOUT_A_FACE_trailer_x500.mp4\" type=\"video/quicktime\" width=\"200\" height=\"50\" autoplay=\"false\">\r\n</object>\r\n\r\n<iframe width=\"500\" height=\"281\" src=\"//www.youtube.com/embed/67-bgSFJiKc#t=84\" frameborder=\"0\" allowfullscreen=\"\"></iframe>\r\n'),(86,1,'2014-07-29 17:43:42','2014-08-04 20:29:08',40,'By Appointment',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'by-appointment',NULL,NULL,'2014-09-11 00:00:00',NULL,NULL,_binary '. . . a bar by Oscar Tuazon opens next door.',_binary '<i>September 9 – October 31, 2014</i><br><br>By appointment, there is a video by Rache Ruepke, a painting by Avery Singer, drawings by Henrik Oleson, a book by Luz Bacher, a recent Kompakt record, and a bar by Oscar Tuazon.<br>',NULL),(87,1,'2014-07-29 17:55:30','2014-12-05 22:54:53',60,'Intermittently',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'intermittently',NULL,'2014-09-09 00:00:00','2015-02-28 00:00:00',NULL,NULL,_binary '. . . Nairy Baghramian invites a special guest.\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n',_binary 'We\'ll be hosting a number of events devised by Nairy Baghramian, who is <a href=\"/apartment/nairy-baghramian-off-broadway-2015\">in residence in our apartment</a>.<br><br>Details and dates to be announced shortly.<br><br><a href=\'calendar\'><</a><br><br>///Fall 2014<br>',NULL),(88,1,'2014-07-29 17:59:57','2014-07-29 18:20:16',NULL,'Thursday, September 9 ',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'thurs